// Fork of "Voronoi Shader with max 8 player" by wouter140. https://shadertoy.com/view/wst3zX
// 2019-10-17 17:30:02

// Screen coords:
//   vec2 in "screen units" relative to the center of viewport
//   y axis pointing up, x axis pointing to the right
//   screen units are chosen so that a square [-1, 1] x [-1, 1] fits into the screen
// World coords: vec2 in units.
// Cameras: vec3, .xy is world coords of the center of the camera, .z is "zoom" (in screen units per world unit)


#define PI 3.1415926535897932384626433832795

#define CAMERA_ZOOM_MIN 0.6
#define CAMERA_ZOOM_MAX 1.0

#define N_PLAYERS 3

vec3 playersColor[N_PLAYERS];
vec3 playersBgColor[N_PLAYERS];


// Inputs of the shader.
vec2 playersPos[N_PLAYERS];  // player positions in world coordinates
vec3 globalCam;             // global camera (zoomed out camera that sees all players)
vec3 playersCam[N_PLAYERS];  // positions of cameras for each player


// *****************************************************
//  Utility functions
// *****************************************************

float atan2(float y, float x) {
	// For some reason GLSL's atan doesn't work as atan2
    // and only returns angles [0, PI).
    if (y > 0.0) {
        return atan(y, x);
    } else {
        return PI + atan(-y, -x);
    }
}

vec2 camScreen2World(vec3 cam, vec2 screenCoord) {
    return cam.xy + screenCoord / cam.z;
}

float camScreen2World(vec3 cam, float screenLength) {
    return screenLength / cam.z;
}

vec2 camWorld2Screen(vec3 cam, vec2 worldCoord) {
    return (worldCoord - cam.xy) * cam.z;
}

float camWorld2Screen(vec3 cam, float worldLength) {
    return worldLength * cam.z;
}

float screen2px(float x) {
    return x * 0.5 * min(iResolution.x, iResolution.y);
}

float camWorld2Px(vec3 cam, float worldLength) {
    return screen2px(camWorld2Screen(cam, worldLength));
}

// Weighted aggregation of the color information for overlapping objects on the same layer.
// The resulting color is a weighted average of all aggreageted colors.
void aggregateColorSiblings(inout vec4 aggColor, inout float aggWeight, vec4 addedColor) {
    if (addedColor.w > 0.0001) {
        float opacity = max(aggColor.w, addedColor.w);        
        aggWeight += addedColor.w;
        aggColor = vec4(mix(aggColor.xyz, addedColor.xyz, addedColor.w / aggWeight), opacity);
    }    
}

// *****************************************************
//  Scene rendering (demo vesion)
// *****************************************************

vec3 renderBackgroundRadialChecker(vec3 cam, vec2 screenCoord) {
    vec2 worldCoord = camScreen2World(cam, screenCoord);

    float radius = length(worldCoord.xy);
    float angularD = atan2(worldCoord.y, worldCoord.x) * radius;

    const float rowWidthGrowthPow = 2.5;    
    const float rowRadiusScale = 100.0;
    // index of a radial checker row.
    float row = ceil(pow(radius * rowRadiusScale, 1.0 / rowWidthGrowthPow));

    float rowOuterRadius = pow(row, rowWidthGrowthPow) / rowRadiusScale;    
    float radialBorderD = abs(radius - rowOuterRadius);
    float columns = 10.0;
    const float minRow = 2.0;
    if (row < minRow + 0.1) {
        row = minRow;
        columns = 4.0;
    } else {
  		float rowInnerRadius = pow(row - 1.0, rowWidthGrowthPow) / rowRadiusScale;
        radialBorderD = min(radialBorderD, abs(radius - rowInnerRadius));

        columns = 10.0;        
    }

    // index of a concentric checker column.
    float columnWidth = (radius * 2.0 * PI) / columns;
    float col = ceil(angularD / columnWidth);

    float concentricBorderD = abs(mod(angularD / columnWidth + 0.5, 1.0) - 0.5) * columnWidth;


    const vec3 white = vec3(1.0);
    const vec3 black = vec3(0.0);
    vec3 midColor = 0.5 * (black + white);

    vec3 color;
    if (mod(row + col, 2.0) > 0.5) {
        color = white;
    } else {
        color = black;
    }

    float borderDPx = camWorld2Px(cam, min(radialBorderD, concentricBorderD));
    float aaColorWeight = smoothstep(0.0, 0.6, borderDPx);

    return mix(midColor, color, aaColorWeight);
}

vec4 renderPlayers(vec3 cam, vec2 screenCoord) {
    vec2 worldCoord = camScreen2World(cam, screenCoord);

    // Player is a 10px at furthest possible zoom and gets bigger when gets closer.
    float radiusPx = 10.0 * max(cam.z, CAMERA_ZOOM_MIN) / CAMERA_ZOOM_MIN;
    const float radiusAaPx = 1.0;

    vec4 color = vec4(0.0);
    float weight = 0.0;
    for(int i = 0; i < N_PLAYERS; i++) {
    	float distPx = camWorld2Px(cam, length(worldCoord - playersPos[i]));

        float playerWeight = 1.0 - smoothstep(radiusPx, radiusPx + radiusAaPx, distPx);
        vec4 playerColor = vec4(playersColor[i], playerWeight);
        aggregateColorSiblings(color, weight, playerColor);
  	}    
    return color;
}

vec3 renderScene(vec3 cam, vec2 screenCoord) {
    vec3 color = renderBackgroundRadialChecker(cam, screenCoord);
    vec4 playersColor = renderPlayers(cam, screenCoord);
    color = mix(color, playersColor.xyz, playersColor.w);

    return color;
}

// *****************************************************
//  Screen partitioning
// *****************************************************

// Returns a number that is guaranteed to be higher than any
// of the distances within Global Camera.
float maxGlobalDist() {
    return camScreen2World(globalCam, 100.0);
}

vec2 closestPlayer(vec2 screenCoord) {
    vec2 worldCoord = camScreen2World(globalCam, screenCoord);



    int minPlayer = 0;
    float minDist = maxGlobalDist();

    for(int i = 0; i < N_PLAYERS; i++) {
    	float dist = length(worldCoord - playersPos[i]);

        if (dist < minDist) {
            minDist = dist;
            minPlayer = i;
        }
  	}



    return vec2(float(minPlayer),minDist);
}

vec3 secondClosestPlayer(vec2 screenCoord) {
    vec2 worldCoord = camScreen2World(globalCam, screenCoord);



    int minPlayer = 0;
    float minDist = maxGlobalDist();
    vec2 minplayersPos;
    vec2 secondMinplayersPos;
    for(int i = 0; i < N_PLAYERS; i++) {
    	float dist = length(worldCoord - playersPos[i]);

        if (dist < minDist) {
            minDist = dist;
            minPlayer = i;
            minplayersPos=playersPos[i] ;
        }
  	}

        int secondMinPlayer = 0;
    float secondMinDist = maxGlobalDist();

    for(int i = 0; i < N_PLAYERS; i++) {
    	float secondDist = length(worldCoord - playersPos[i]);

        if (secondDist < secondMinDist && i!=minPlayer) {
            secondMinDist = secondDist;
            secondMinPlayer = i;
                        secondMinplayersPos=playersPos[i] ;

        }
  	}

    return       vec3(float(secondMinPlayer),secondMinDist,length(secondMinplayersPos-minplayersPos));

}

// *****************************************************
//  Faking inputs
// *****************************************************

vec2 initPlayerPos(vec2 basePos, float radius, float basePhase, float timeC) {
    vec2 center = basePos - radius * vec2(cos(basePhase), sin(basePhase));
    float phase = basePhase + iTime * timeC;
    return center + radius * vec2(cos(phase), sin(phase));
}

void fakePlayers() {
    const float timeScale = 0.2;

    for(int i = 0; i < N_PLAYERS; i++) {
        switch(i) {
            case 0:
            playersColor[i] = vec3(0.957, 0.263, 0.212);  // red
            playersPos[i] = initPlayerPos(vec2(0.2, 0.1), 2.0, PI, 1.0 * timeScale);
            break;

            case 1:
            playersColor[i] = vec3(0.129, 0.588, 0.953);  // blue
            playersPos[i] = initPlayerPos(vec2(-0.2, 0.1), 1.0, 3.0 * PI * 0.5, 3.0 * timeScale);
            break;

            case 2:
            playersColor[i] = vec3(0.298, 0.686, 0.314);  // green
            playersPos[i] = initPlayerPos(vec2(-0.2, -0.1), 3.0, 0.0, 0.5 * timeScale);
            break;

            case 3:
            playersColor[i] = vec3(0.612, 0.153, 0.69);   // purple
            playersPos[i] = initPlayerPos(vec2(0.2, -0.1), 2.0, 3.0 * PI / 4.0, 1.0 * timeScale);
            break;


            case 4:
            playersColor[i] = vec3(1, 0.153, 0.69);   
            playersPos[i] = initPlayerPos(vec2(0.2, -0.1), 2.0, 3.0 * PI / 5.0, 1.0 * timeScale);
            break;

            case 5:
            playersColor[i] = vec3(0.612, 1, 0.69);   
            playersPos[i] = initPlayerPos(vec2(0.2, -0.1), 1.0, 3.0 * PI / 10.0, 1.0 * timeScale);
            break;

            case 6:
            playersColor[i] = vec3(0.612, 0.153, 1);   
            playersPos[i] = initPlayerPos(vec2(0.2, -0.1), 3.0, 3.0 * PI / 14.0, 1.0 * timeScale);
            break;

            case 7:
            playersColor[i] = vec3(0.612, 0.553, 1);   
            playersPos[i] = initPlayerPos(vec2(0.2, -0.1), 2.0, 3.0 * PI / 34.0, 1.0 * timeScale);
            break;


            case 8:
            playersColor[i] = vec3(0.957, 0.263, 0.212);  // red
            playersPos[i] = initPlayerPos(vec2(0.8, 0.5), 2.0, PI, 1.0 * timeScale);
            break;

            case 9:
            playersColor[i] = vec3(0.129, 0.588, 0.953);  // blue
            playersPos[i] = initPlayerPos(vec2(-0.8, -0.1), 1.0, -3.0 * PI * 0.5, 3.0 * timeScale);
            break;

            case 10:
            playersColor[i] = vec3(0.298, 0.686, 0.314);  // green
            playersPos[i] = initPlayerPos(vec2(-0.3, 0.1), 3.0, 0.0, 0.5 * timeScale);
            break;

            case 11:
            playersColor[i] = vec3(0.612, 0.153, 0.69);   // purple
            playersPos[i] = initPlayerPos(vec2(-0.4, 0.1), 2.0, 3.0 * PI / -4.0, 1.0 * timeScale);
            break;

            case 12:
            playersColor[i] = vec3(1, 0.153, 0.69);   
            playersPos[i] = initPlayerPos(vec2(0.3, -0.7), 2.0, -3.0 * PI / 5.0, 1.0 * timeScale);
            break;

            case 13:
            playersColor[i] = vec3(0.612, 1, 0.69);   
            playersPos[i] = initPlayerPos(vec2(-0.92, -0.1), 1.0, -3.0 * PI / -10.0, 1.0 * timeScale);
            break;

            case 14:
            playersColor[i] = vec3(0.612, 0.153, 1);   
            playersPos[i] = initPlayerPos(vec2(0.29, 0.1), 3.0, 3.0 * PI / -4.0, 1.0 * timeScale);
            break;

            case 15:
            playersColor[i] = vec3(0.612, 0.553, 1);   
            playersPos[i] = initPlayerPos(vec2(0.62, -0.31), 2.0, 3.0 * PI / 44.0, 1.0 * timeScale);
            break;
        }
    }
}


void fakeGlobalCamera() {
    vec2 minPlayerCoord = playersPos[0];
    vec2 maxPlayerCoord = playersPos[0];
    for(int i = 1; i < N_PLAYERS; i++) {
        minPlayerCoord = min(minPlayerCoord, playersPos[i]);
        maxPlayerCoord = max(maxPlayerCoord, playersPos[i]);
  	}
    const float cameraMarginScreen = 0.4;
    // (maxPlayerCoord.x - minPlayerCoord.x) * zoom + 2.0 * cameraMarginScreen = iResolution.x / screen2px(1.0);
    vec2 zoom = (iResolution.xy / screen2px(1.0) - 2.0 * cameraMarginScreen) / (maxPlayerCoord - minPlayerCoord);
	globalCam = vec3((minPlayerCoord + maxPlayerCoord) * 0.5, min(min(zoom.x, zoom.y), CAMERA_ZOOM_MAX));
}

void fakePlayerCameras() {
    if (globalCam.z > CAMERA_ZOOM_MIN) {
        for(int i = 0; i < N_PLAYERS; i++) {
            playersCam[i] = globalCam;
        }
    } else {
        for(int i = 0; i < N_PLAYERS; i++) {
            vec2 playerPosScreen = camWorld2Screen(globalCam, playersPos[i]);
            playersCam[i] = vec3(playersPos[i] - playerPosScreen / CAMERA_ZOOM_MIN, CAMERA_ZOOM_MIN);
        }
    }
}

void adjustPlayerCameras(float uniformCamZoom) {

    for(int i = 0; i < N_PLAYERS; i++) {
        playersBgColor[i] = playersColor[i];
    }
    float mergeDistMin = 0.6 * CAMERA_ZOOM_MIN;
    float mergeDistMax = 0.8 * CAMERA_ZOOM_MIN;
    for (int k = 0; k < 2; k++) {
        // Moving cameras closer to each other.
        for(int i = 0; i < N_PLAYERS; i++) {
            for(int j = 0; j < N_PLAYERS; j++) {
                if (i < j) {
                    vec2 camPosI = playersCam[i].xy;
                    vec2 camPosJ = playersCam[j].xy;
                    float camDistScreen = length(camPosI - camPosJ) * uniformCamZoom;
                    float mergeWeight = 0.5 * (1.0 - pow(smoothstep(mergeDistMin, mergeDistMax, camDistScreen), 4.0));
                    playersCam[i].xy = mix(camPosI, camPosJ, mergeWeight);
                    playersCam[j].xy = mix(camPosJ, camPosI, mergeWeight);

                    vec3 bgColorI = playersBgColor[i];
                    vec3 bgColorJ = playersBgColor[j];
                    playersBgColor[i] = mix(bgColorI, bgColorJ, mergeWeight);
                    playersBgColor[j] = mix(bgColorJ, bgColorI, mergeWeight);
                }            
            }
        }
        mergeDistMin *= 0.5;
        mergeDistMax *= 0.5;
    }
}

// *****************************************************
//  Demos
// *****************************************************

vec4 mainSimpleDemo(vec2 screenCoord) {
    fakePlayers();    

    float zoom = (CAMERA_ZOOM_MAX + CAMERA_ZOOM_MIN) * 0.5; // x axis spans [-10, 10]
    zoom = zoom - (CAMERA_ZOOM_MAX - CAMERA_ZOOM_MIN) * 0.5 * cos(iTime * 0.3);  // animate zoom
    const float camRadius = 0.2;
	vec3 camera = vec3(camRadius * cos(iTime), camRadius * sin(iTime), zoom);

    return vec4(renderScene(camera, screenCoord), 1.0);
}

vec4 mainGlobalCameraDemo(vec2 screenCoord) {
    fakePlayers();    
    fakeGlobalCamera();

    vec3 color = renderScene(globalCam, screenCoord);
    int player = int(closestPlayer(screenCoord)[0]);
    for(int i = 0; i < N_PLAYERS; i++) {
        if (player == i) {
        	color = mix(color, playersColor[i], 0.4);
        }
    }

    return vec4(color, 1.0);
}

vec4 mainSplitScreenDemo(vec2 screenCoord) {
    fakePlayers();    
    fakeGlobalCamera();
    fakePlayerCameras();

    // We assume that all player cams have the same zoom.
    float uniformCamZoom = playersCam[0].z;
    adjustPlayerCameras(uniformCamZoom);

    vec2 res = closestPlayer(screenCoord);
    int i= int(res[0]);
    vec3 res2 = secondClosestPlayer(screenCoord);
    int i2= int(res2[0]);


    float dist=res[1];
    float sumDist=(res[1]+res2[1]);
    float pairmix=dist/sumDist;
    float pairDist=res2[2];
    float bgmix=smoothstep(0.,.9, min(1.,1./pairDist));
    vec3 closecolor=(playersBgColor[i ]+playersBgColor[i2 ])/2.;

    vec3 color;

   {
        	color = renderScene(playersCam[i], screenCoord);
	               vec3 bgcolor = mix( playersBgColor[i ]  ,closecolor,bgmix);
                   	color  = mix(color , bgcolor, 0.4);        }



    vec3 color2;

   {
        	color2 = renderScene(playersCam[i2], screenCoord);
        	 vec3 bgcolor = mix( playersBgColor[i2 ]  ,closecolor,bgmix);
                   	color2 = mix(color2, bgcolor, 0.4);

        }





    color=mix(color,color2,smoothstep(0.49,.51, pairmix) );


    return vec4(color, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 screenCoord = (fragCoord.xy - 0.5 * iResolution.xy) / screen2px(1.0);
    // vec4 color = mainSimpleDemo(screenCoord);
    // vec4 color = mainGlobalCameraDemo(screenCoord);
    vec4 color = mainSplitScreenDemo(screenCoord);
	fragColor = vec4(color.xyz, 1.0);
}

