vec3 swing(float t){  return vec3(0.,sin (smoothstep(0.,1. , mod(t,1.0))*TAU),0.);} 
vec3 sway(float t){  return vec3(    sin ( smoothstep(0.,1. ,mod(t,1.0))*TAU),0.,0.);} 

