// Fork of "The Walking Raymarcher" by xorxor. https://shadertoy.com/view/Mt3XWH
// 2018-11-11 15:37:21


// tracer from  https://shadertoy.com/view/Mt3XWH
// sdPentagon from https://www.shadertoy.com/view/llVyWW
// roundrect forked fromm https://www.shadertoy.com/view/4sjyRz

float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return  (max(d.x,d.y) );
}

 float sdPentagon( in vec2 p, in float r )
{
    const vec3 k = vec3(0.809016994,0.587785252,0.726542528); // pi/5: cos, sin, tan

    // reflections
    p.y = -p.y;
    p.x = abs(p.x);
    p -= 2.0*min(dot(vec2(-k.x,k.y),p),0.0)*vec2(-k.x,k.y);
    p -= 2.0*min(dot(vec2( k.x,k.y),p),0.0)*vec2( k.x,k.y);

	// side of polygon
    return length(p-vec2(clamp(p.x,-r*k.z,r*k.z),r))*sign(p.y-r);
}

float smin( float a, float b )
{
    const float k = .21;
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);

}

float smax( float d1, float d2 ,float k ) {

    float h = clamp( 0.5 - 0.5*(d2+d1)/k, 0.0, 1.0 );
    return mix( d2, -d1, h ) + k*h*(1.0-h); }


float roundrect(vec2 p, vec2 c, vec2 r) {

    p = abs(p) - c +r ;
	if (p.x >= 0.0 && p.y >= 0.0)
        return length(p)-r.x;
    else
        return max(p.x, p.y)-r.x;
}

float shape1(vec2 p){

float d= sdPentagon(p-vec2(-2.+-2.*sin(iTime*4.),0),1.3);
      d=smin(d, sdPentagon(p-vec2(2,0.+2.*sin(iTime*2.) ),1.3) );

    d=smin(d,sdBox(p-vec2(0,-6),vec2(3) ) );

    return (d );
    }
float shape2(vec2 p){
p=p+vec2(0.2+sin(iTime*3.)*2.,0);

float d= roundrect(p-vec2(1.75,0),vec2(2.7,1.),vec2(.2));


    d=min(d,roundrect(p-vec2(2.2,0),vec2(3.3,0.5),vec2(.2))  );


    d=min(d,roundrect(p-vec2(-0.0,14),vec2(0.8,1),vec2(.3)));
    d=min(d,roundrect(p-vec2(-0,7), vec2(1,7.),vec2(.6)));
    d=min(d, length(p-vec2(1,1))-1.1);
    d=smax(  (length(p-vec2(1.2,8))-.7),d,0.23);
    d=smin(d, (length(p-vec2(1.0,5))-.6));
      d=smax((length(p-vec2(3.1,11))-2.7),d,0.24);



    return(d );
        }


vec2 map( vec3 p )
{
    float plane = abs( p.y + 0.9 );




   float w =  shape1(    p.xz);
   float d =  shape2(vec2(w,p.y));


    return ( d < plane ) ? vec2( d, 1.0 ) : vec2( plane, 2.0 );
}

float calcShadow( vec3 ro, vec3 rd, float mint, float maxt )
{
    float t = mint;
    float res = 1.0;
    for ( int i = 0; i < 10; i++ )
    {
        float h = map( ro + rd * t ).x;
        res = min( res, 1.1 * h / t );
        t += h;
        if ( ( h < 0.001 ) || ( t > maxt ) )
        {
            break;
        }
    }
    return clamp( res, 0.0, 1.0 );
}

vec2 trace( vec3 ro, vec3 rd )
{
    const float kTMin = 0.01;
    const float kTMax = 200.0;
    const float kEps = 0.001;

    float t = kTMin;
    vec2 res;
    for ( int i = 0; i < 70; i++ )
    {
        vec3 pos = ro + rd * t;
        res = map( pos );
        if ( ( res.x < kEps ) || ( t > kTMax ) )
        {
            break;
        }
        t += res.x * 0.5;
    }

    if ( t < kTMax )
    {
        return vec2( t, res.y );
    }
    else
    {
        return vec2( -1.0 );
    }
}

vec3 calcNormal( vec3 p )
{
    const vec2 e = vec2( 0.005, 0 );
    float dp = map( p ).x;
    return normalize( vec3( dp - map( p - e.xyy ).x,
                            dp - map( p - e.yxy ).x,
                            dp - map( p - e.yyx ).x ) );
}

mat3 calcCamera( vec3 eye, vec3 target )
{
    vec3 cw = normalize( target - eye );
    vec3 cu = cross( cw, vec3( 0, 1, 0 ) );
    vec3 cv = cross( cu, cw );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 mo = vec2( 0.95, -0.2 );
    if ( iMouse.z > 0.5 )
    {
        mo = 10.0 * iMouse.xy / iResolution.xy - 1.0;
        mo *= 3.14159 * vec2( 0.4, 0.1 );
    }
    mo += 3.14159 * 2.5;

    vec3 eye = vec3( 40.0 * cos( mo.x ), 30.0 + 20.0 * cos( mo.y ), 40.0 * sin( mo.x ) );
    vec3 target = vec3( 0.0, 6.0, 0.0 );

    mat3 cam = calcCamera( eye, target );

 	vec2 uv = ( fragCoord.xy - 0.5 * iResolution.xy ) / iResolution.y;
    vec3 rd = cam * normalize( vec3( uv, 2.0 ) );

    vec3 col = vec3( 1.0 );

    vec2 res = trace( eye, rd );
    if ( res.x > 0.0 )
    {
        vec3 pos = eye + rd * res.x;
        vec3 nor = calcNormal( pos );
        vec3 ldir = normalize( vec3( -10.5, 20.8, 24.0 ) );

        if ( res.y < 1.5 )
        {
        	col = 0.5 + 0.5 * nor;
            float dif = max( dot( nor, ldir ), 0.0 );
            vec3 ref = reflect( rd, nor );
            float spe = pow( clamp( dot( ref, ldir ), 0.0, 1.0 ), 15.0 );

            col *= ( 0.3 + 0.7 * dif );
            float edge = pow( 1.0 - dot( -rd, nor ), 1.1 );
        	col += 0.8 * edge + spe;
        }

        float sh = calcShadow( pos, ldir, 0.1, 30.0 );
        col *= ( 0.5 + sh );
    }




    if (fract(iTime/10.)<0.33) {
       // col=vec3( shape1(uv*20.-vec2(0,4))>0. )

                float len = shape1(uv*20.-vec2(0,4))/10.;

    //distance field coloring by iq https://www.shadertoy.com/view/4lcBWn
       col = vec3(1.0) - sign(len)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-iResolution.y*0.008*abs(len));
	col *= 0.8 + 0.2*cos(iResolution.y*0.3*abs(len));
	col = mix( col, vec3(1.0), max(1.0-abs(len)*iResolution.y*0.2,0.0));
	fragColor = vec4(col*col, 1.0);
            }
    if (fract(iTime/10.)<0.165) {
          // col=vec3( shape2(uv*20.-vec2(0,-8))>0. );

            float len = shape2(uv*20.-vec2(0,-7))/10.;

    //distance field coloring by iq https://www.shadertoy.com/view/4lcBWn
       col = vec3(1.0) - sign(len)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-iResolution.y*0.008*abs(len));
	col *= 0.8 + 0.2*cos(iResolution.y*0.3*abs(len));
	col = mix( col, vec3(1.0), max(1.0-abs(len)*iResolution.y*0.2,0.0));
		if (abs(uv.x)<0.005)col=vec3(1.);

	fragColor = vec4(col*col, 1.0);   
    }
    fragColor = vec4( col, 1.0 );
}

