// signed distance to a quadratic bezier
float sdBezier(vec2 pos, vec2 A, vec2 B, vec2 C)
{    
    vec2 a = B - A;
    vec2 b = A - 2.0*B + C;
    vec2 c = a * 2.0;
    vec2 d = A - pos;

    float kk = 1.0 / dot(b,b);
    float kx = kk * dot(a,b);
    float ky = kk * (2.0*dot(a,a)+dot(d,b)) / 3.0;
    float kz = kk * dot(d,a);      

    float res = 0.0;

    float p = ky - kx*kx;
    float p3 = p*p*p;
    float q = kx*(2.0*kx*kx - 3.0*ky) + kz;
    float h = q*q + 4.0*p3;

    if(h >= 0.0) 
    { 
        h = sqrt(h);
        vec2 x = (vec2(h, -h) - q) / 2.0;
        vec2 uv = sign(x)*pow(abs(x), vec2(1.0/3.0));
        float t = uv.x + uv.y - kx;
        t = clamp( t, 0.0, 1.0 );

        // 1 root
        vec2 qos = d + (c + b*t)*t;
        res = length(qos);
    }
    else
    {
        float z = sqrt(-p);
        float v = acos( q/(p*z*2.0) ) / 3.0;
        float m = cos(v);
        float n = sin(v)*1.732050808;
        vec3 t = vec3(m + m, -n - m, n - m) * z - kx;
        t = clamp( t, 0.0, 1.0 );

        // 3 roots
        vec2 qos = d + (c + b*t.x)*t.x;
        float dis = dot(qos,qos);

        res = dis;

        qos = d + (c + b*t.y)*t.y;
        dis = dot(qos,qos);
        res = min(res,dis);

        qos = d + (c + b*t.z)*t.z;
        dis = dot(qos,qos);
        res = min(res,dis);

        res = sqrt( res );
    }

    return res;
}

vec2 fan(  vec2 pos,   float q) 
{
    q = q / 180. * 3.14159265;
    float ang = atan(pos.x, pos.y),
    len = length(pos.xy);
    ang = mod(ang + q/2., q) - q/2.;
    pos.xy = len * vec2(sin(ang), cos(ang));
    return pos;
}

/** repeat the position in 'pos' every 'q' units */
vec2 repeat(  vec2 pos,   vec2 q) 
{
    pos.xy = mod(pos.xy + q/2., q) - q/2.;
    return pos;
}

vec2 mirror( vec2 p, vec2 v1, vec2 v2 )
{
 	vec2   s = v2-v1 ,
           n = normalize(vec2( s.y, -s.x )) ;
    float  d = dot(p-v1,n) ;

    return p-max(0.0,2.0*d)*n ;
}


vec3 map(vec2 p)
{      

    p=repeat( p,  vec2(9)); 
    p=mirror( p, vec2 (0,4), vec2 (1,4)); 


      p=repeat( p,  vec2(3)); 
    p=mirror( p, vec2 (0,0), vec2 (1,1)); 

      p=repeat( p,  vec2(1.5)); 
    p=fan( p, 60.); 


//p=fan( p ,  (30.)); 

 //  p=repeat( p,  vec2(2.5,2)); 
