//  p=mirror( p, vec2 (0,0), vec2 (1,0) ); 


    vec2 A=vec2(0.1,0.3);
    vec2 B=vec2(0.0,0.8);
    vec2 C=vec2(0.4,0.8);
 float   d=  sdBezier(p,   A,   B,  C);
