vec2 foliage( in vec3 pos) {

    pos.y=pos.y-pathterrain(pos.x,pos.z);

    float Strand, Spread, Lift, Size;
    float sqr = 1.;
    float d = length(pos.xz)+1.;
    vec3 pfract = fract(pos / sqr) * sqr;
    vec3 pround = round(pos / sqr) * sqr + vec3(sqr * 0.5, 0, sqr * 0.5);
    vec3 mos = vec3(pfract.x, pos.y, pfract.z);

    float i = (pos - pfract).x;
    float j = (pos - pfract).z;

    Strand = 0.01 + fract( sin(i * 12. + j*9.9) * 9.219869) * 0.0325;
    Spread = 0. + fract( sin (i * 12. + j*9.9) * 99.24927) * .3;
    Lift = .2 + fract(  sin(i * 42. + j*32.9) * 199.210930) * .5;
    Size = .2 + fract( sin (i * 2. + j) * 99.) * .7;

    d = min(d, plant(mos - vec3(sqr * 0.5, 0., sqr * 0.5), vec4(Strand, Spread * 1.1, Lift, Size)));

      d = max(d, -(length(pfract.xz - vec3(sqr * 0.5, 0., sqr * 0.5).xz) -0.01));

    return vec2(d, 3);

} 

vec2 map( in vec3 pos) {
	vec2 res = vec2(pos.y-pathterrain(pos.x,pos.z), 1.0);
	//vec2 bush =  foliage(pos);
    //res=res.x<bush.x?res:bush;

 	return res;
}






vec2 castRay( in vec3 ro, in vec3 rd) {
	float tmin = .10;
	float tmax = 50.0;
	float t = tmin;
	float m = -1.0;
	for (int i = 0; i < 80; i++) {
		float precis = 0.0001 * t;
		vec2 res = map(ro + rd * t);
		if (res.x < precis || t > tmax) break;
		t += res.x * .7;
		m = res.y;
	}
	if (t > tmax) m = -1.0;
	return vec2(t, m);
}
float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax) {
	float res = 1.0;
	float t = mint;
	for (int i = 0; i < 32; i++) {
		float h = map(ro + rd * t).x;
		res = min(res, 8.0 * h / t);
		t += clamp(h, 0.02, 0.10);
		if (res < 0.005 || t > tmax) break;
	}
	return clamp(res, 0.0, 1.0);
}
vec3 calcNormal( in vec3 pos) {
	vec2 e = vec2(1.0, -1.0) * 0.5773 * 0.0005;
	return normalize(e.xyy * map(pos + e.xyy).x +
		e.yyx * map(pos + e.yyx).x +
		e.yxy * map(pos + e.yxy).x +
		e.xxx * map(pos + e.xxx).x);
}
float calcAO( in vec3 pos, in vec3 nor) {
	float occ = 0.0;
	float sca = 1.0;
	for (int i = 0; i < 5; i++) {
		float hr = 0.01 + 0.12 * float(i) / 4.0;
		vec3 aopos = nor * hr + pos;
		float dd = map(aopos).x;
		occ += -(dd - hr) * sca;
		sca *= 0.95;
	}
	return clamp(1.0 - 3.0 * occ, 0.0, 1.0);
}

