	vec3 tot = vec3(0.0);
 #	if AA > 1
	for (int m = 0; m < AA; m++)
		for (int n = 0; n < AA; n++) {
			// pixel coordinates
			vec2 o = vec2(float(m), float(n)) / float(AA) - 0.5;
			vec2 p = (-iResolution.xy + 2.0 * (fragCoord + o)) / iResolution.y;
 # else
				vec2 p = (-iResolution.xy + 2.0 * fragCoord) / iResolution.y;
 # endif
			// camera	
			vec3 ta = timefly(time)  ;
			vec3 ro = vec3(0,1,0)+timefly(time-1.) 
                + vec3(-0.5 + 3.5 * cos(  6.0 * mo.x),
				1.0 + 2.0 * mo.y,
				0.5 + 4.0 * sin( 6.0 * mo.x))*(2.*(sin(iTime*.2)+1.2));
			// camera-to-world transformation
			mat3 ca = setCamera(ro, ta, 0.0);
			// ray direction


			vec3 rd = ca * normalize(vec3(p.xy, 2.5));

                        vec3 centerray=normalize(ca * normalize(vec3(0,0, 2.5)));
                  float uppness=( abs(dot(mix(rd,centerray,-0.1),vec3(0,1,0))));
            uppness*=uppness;

            uppness=1.-uppness; 
                UV= centerray.xz*uppness*1.1;
			// render	
			vec3 col = render(ro, rd);
			// gamma
			col = pow(col, vec3(0.4545));
			tot += col;
 # if AA > 1
		}
	tot /= float(AA * AA);
 #	endif
	fragColor = vec4(tot, 1.0);
