vec2 hash( vec2 p ) { p=vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))); return fract(sin(p)*18.5453); }

// return distance, and cell id
vec2 voronoi( in vec2 p )
{
    vec2 n = floor( p ) ;
    vec2 f = fract( p ) ;

	vec3 m = vec3( 8.0 ); 
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<= 1        -abs(j)
        ; i++ )
    {
        vec2  g = vec2( float(i) , float(j) ) ;
        vec2  o =   hash( n + g +vec2(floor(mod(p.y  +float(j) ,2.))*.5,0.5 ) )  ;
     vec2  r = g - f 
          +vec2(1.-floor(mod(p.y  +float(j) ,2.))*.5,0.5)

          + o
         ;
 		float d = dot( r, r );
        if( d<m.x )
            m = vec3( d, o );
    }

    return vec2( sqrt(m.x), m.y+m.z );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy/max(iResolution.x,iResolution.y);
    vec2 asp =   iResolution.xy/max(iResolution.x,iResolution.y) ;
    p=p-(asp*0.5);
    // computer voronoi patterm
    vec2 c = voronoi( (14.0+6.0*sin(0.2*iTime))*p );

    // colorize
    vec3 col = 0.5 + 0.5*cos( c.y*6.2831 + vec3(0.0,1.0,2.0) );	
    col *= clamp(1.0 - 0.4*c.x*c.x,0.0,1.0);
    col -= (1.0-smoothstep( 0.08, 0.09, c.x));

    fragColor = vec4( col, 1.0 );
}

