 vec4 NormQuat(vec4 q)
{
    float lenSQ = dot(q, q);
    float invLenSQ = 1./lenSQ;
    return q*invLenSQ;
}

vec4 BuildQuat(vec3 axis, float angle)
{
    angle *= 0.5;
    float s = sin(angle);
    return NormQuat(vec4(axis*s, cos(angle)));
}

vec4 MulQuat(vec4 q1, vec4 q2)
{
    vec4 res = vec4(0.);
    res.w   = q1.w*q2.w - dot(q1.xyz, q2.xyz);
    res.xyz = q1.w*q2.xyz + q2.w*q1.xyz + cross(q1.xyz, q2.xyz);
    return NormQuat(res);
}



vec3 Rotate(vec4 q, vec3 v)
{
    vec3 t = 2.*cross(q.xyz, v);
    return v + q.w*t + cross(q.xyz, t);
}



float fromTo(vec3 p,vec3 q)
{
return acos(dot(p,q));

}

vec4 quatLookAt(vec3 forward,vec3 up){
    forward=normalize(forward);
    up=normalize(up);
return BuildQuat(cross(forward,up),fromTo(forward,up));
}


