float gauss(float x, float s) {
    return exp(-.5*(x*x)/(s*s)); 
}
float gauss(float x) {
    return exp(-.5*x*x); 
}
float gauss(float s,vec2 D) {
	float d = dot(D,D)/(s*s);
	return exp(-.5*d); 
}

#define SQR(x) ((x)*(x))



float swirlgabor(vec2 pos, vec2 k, float gaussF, float phi) {
    float p=  length(pos) ;
    float g = gauss(p, 1./gaussF);
    float a=  atan(k.y,k.x)+(2.+sin(phi))*1.2*6.28*smoothstep(gaussF*0.65, -2.,p) ;
    vec2 k2=vec2(sin(a),cos(a))*length(k);
    float s = clamp ( -1.,1.,.5* sin(2.*PI*dot(pos,k2)  ));


	return  (g* s);
}

float polargabor(vec2 pos, vec2 k, float gaussF, float phi) {
	float g = gauss(length(pos), 1./gaussF);
    float s = clamp ( -1.,1., sin( length(pos)*2.*PI*  max(k.x,k.y) - phi) );

	return  (g* s);
}


float sdLine( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.1, .9 );
    return length( pa - ba*h );
}


float sdvoluteLine( in vec2 p, in vec2 a, in vec2 b ,float curla ,float curlb )
{
    float gaussF=   length(b-a)  ;
    vec2 pa=p-a, ba = b-a, pb=p-b;
    float h = clamp( dot(pa,ba)/dot(ba,ba), -0., 1. );

    float pal=  length(pa) ;
    float pbl=  length(pb) ;
    float bal=  length(ba) ;

    float ga = gauss(pal,  (bal*0.5)/gaussF)*gaussF;
    float gb = gauss(pbl,  (bal*0.5)/gaussF)*gaussF;

    float tha= 1.57  +  atan(pa.y,pa.x)       + curla*6.28*   ga        ;    
    float thb= 1.57  +  atan(pb.y,pb.x)       + curlb*6.28*   gb         ;

    vec2 pap=vec2( sin(tha),cos(tha) )*pal+a;
    vec2 pbp=vec2( sin(thb),cos(thb) )*pbl+b;


    return sdLine(  mix(pap,pbp,( h)),   a,   b );
}




