const float PI = acos(-1.0);

float smin(float a, float b) {
    const float k = 1.921;
    float h = clamp(0.5 + 0.5 * (b - a) / k, 0.0, 1.0);
    return mix(b, a, h) - k * h * (1.0 - h);

}

float smax(float d1, float d2, float k) {

    float h = clamp(0.5 - 0.5 * (d2 + d1) / k, 0.0, 1.0);
    return mix(d2, -d1, h) + k * h * (1.0 - h);
}

float spiral(vec2 p, float a, float b, float w, float angle, float rmax) {
    //https://swiftcoder.wordpress.com/2010/06/21/logarithmic-spiral-distance-field/
    //# calculate the target radius and theta
    float r = length(p);
    float t = atan(p.y, p.x) + angle;

    //# early exit if the point requested is the origin itself
    //# to avoid taking the logarithm of zero in the next step
    if (r == 0.)
        return 0.;

    //# calculate the floating point approximation for n
    //# calculate n
    float n = floor((log(r / a) / b - t) / (2.0 * PI));

    //# find the two possible radii for the closest point
    float lower_r = a * exp(b * (t + 2.0 * PI * n));
    float upper_r = lower_r * exp(2.0 * PI * b);

    //# return the minimum distance to the target point
    return
    max((r - rmax),
        (min(abs(upper_r - r), abs(r - lower_r)) - w)
    );

}


float sdLine( in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0., 1.);
    return length(pa - ba * h);
}



float sdvolute( in vec2 p, in vec2 a, in vec2 b, float curla, float curlb) {
    vec2 pa = p - a, ba = b - a, pb = p - b;
    float bal = length(ba);
    float pal = length(pa);
    float pbl = length(pb);
    float h = clamp(dot(pa, ba) / dot(ba, ba), -0., 1.);
    float theta = atan(ba.y, ba.x);

    float d1 = spiral(pa, 1., .5, 0.75, PI + .9, 6.);
    float d2 = spiral(pb, 1., .5, 0.75, .9, 6.);

    //float   dl=abs(sdLine( p, a, b )-bal*0.5)+1.;
    // float d12mix=mix(d1,d2,smoothstep(0.,1.,smoothstep(0.,1.,smoothstep(0.,1.,h))));
    return smin(d1, d2);
}










float roundrect(vec2 p, vec2 c, vec2 r) {

    p = abs(p) - c + r;
    if (p.x >= 0.0 && p.y >= 0.0)
        return length(p) - r.x;
    else
        return max(p.x, p.y) - r.x;
}

float shape1(vec2 p) {

    float d = 0.25 * sdvolute(p * 1.,
        vec2(-4. + sin(iTime * 0.7) * 0.5, 1.8 + 1. * cos(iTime * 0.5)),
        vec2(4. - sin(iTime * 0.7) * 0.5, -1.8 - 1. * cos(iTime * 0.5)),
        sin(iTime * 0.3) * 0.75,
        sin(iTime * 0.5) * 0.75);


    return (d);
}
float shape2(vec2 p) {

    float d = roundrect(p - vec2(-4.1, 0), vec2(4., .6), vec2(.05));


    d = min(d, roundrect(p - vec2(-.085, 0.), vec2(.025, .8), vec2(.03)));
    d = min(d, roundrect(p - vec2(-.185, 0.), vec2(.025, .7), vec2(.03)));
    d = min(d, length(p - vec2(-.36, 0.)) - .4);




    return (d);
}


vec2 map(vec3 p) {

    p = vec3((fract(p.x / 28.)) * 28., p.y, (fract(p.z / 28.)) * 28.); // repeat
    p = p - vec3(14, 0, 9); //translate

    //p=p-vec3(10,0,10);

    p.xz = abs(p.xz); //mirror
    p = p - vec3(3.5, 0, 3.5);

    //p=p-vec3(10,0,5);

    float an = .66; //additional angle
    p = vec3((p.x * cos(an) - p.z * sin(an)), p.y, (p.z * cos(an) + p.x * sin(an)));
    float plane = abs(p.y + 6.6);//making geound floor

    p = p.yxz; // orienting upright
    p.x = -p.x; //flip
    p.z = -p.z; //flip

    float w = shape1(p.xz); //building shape on shape
    float d = shape2(vec2(w, p.y));


    return (d < plane) ? vec2(d, 1.0) : vec2(plane, 2.0);
}

float calcShadow(vec3 ro, vec3 rd, float mint, float maxt) {
    float t = mint;
    float res = 1.0;
    for (int i = 0; i < 10; i++) {
        float h = map(ro + rd * t).x;
        res = min(res, 1.1 * h / t);
        t += h;
        if ((h < 0.001) || (t > maxt)) {
            break;
        }
    }
    return clamp(res, 0.0, 1.0);
}

vec2 trace(vec3 ro, vec3 rd) {
    const float kTMin = 0.01;
    const float kTMax = 200.0;
    const float kEps = 0.001;

    float t = kTMin;
    vec2 res;
    for (int i = 0; i < 170; i++) {
        vec3 pos = ro + rd * t;
        res = map(pos);
        if ((res.x < kEps) || (t > kTMax)) {
            break;
        }
        t += res.x * 0.5;

    }

    if (t < kTMax) {
        return vec2(t, res.y);
    } else {
        return vec2(-1.0);
    }
}

vec3 calcNormal(vec3 p) {
    const vec2 e = vec2(0.005, 0);
    float dp = map(p).x;
    return normalize(vec3(dp - map(p - e.xyy).x,
        dp - map(p - e.yxy).x,
        dp - map(p - e.yyx).x));
}

mat3 calcCamera(vec3 eye, vec3 target) {
    vec3 cw = normalize(target - eye);
    vec3 cu = cross(cw, vec3(0, 1, 0));
    vec3 cv = cross(cu, cw);
    return mat3(cu, cv, cw);
}

