vec2 scene( in vec3 pos) {

	vec2 res = paint(sdSphere(pos, 1.),1.);
	vec2 plane = paint(sdPlane(pos, vec4(0, 1, 0, 0.5)), 1.);
	res = opSmoothUnion(res, plane, 0.1);

	return res;
}
//------------------------------------------------------------------------------
// Materials
//------------------------------------------------------------------------------

float checkersGradBox( in vec2 p) {
	// filter kernel
	vec2 w = fwidth(p) + 0.001;
	// analytical integral (box filter)
	vec2 i = 2.0 * (abs(fract((p - 0.5 * w) * 0.5) - 0.5) - abs(fract((p + 0.5 * w) * 0.5) - 0.5)) / w;
	// xor pattern
	return 0.5 - 0.5 * i.x * i.y;
}

vec3 normal( in vec3 position) {
	vec3 epsilon = vec3(0.001, 0.0, 0.0);
	vec3 n = vec3(
		scene(position + epsilon.xyy).x - scene(position - epsilon.xyy).x,
		scene(position + epsilon.yxy).x - scene(position - epsilon.yxy).x,
		scene(position + epsilon.yyx).x - scene(position - epsilon.yyx).x);
	return normalize(n);
}

matr getMaterial(vec3 pos, float id, float distance, float occ, vec3 n, vec3 origin, vec3 direction) {
   // default material gets copied and normal is added.
    // user defined materila function is called here
	float i = float(material.length());
	int idint = int(round(clamp(id, 0., i)));
	matr ret;

	ret = material[idint];
	ret.normal = n;


	if (idint == 1) {
		float f = checkersGradBox(2.2 * pos.xz);
		ret.rgb = 0.3 + f * material[idint].rgb ;

	}



	return ret;
}
