float shadow( in vec3 origin, in vec3 direction) {
	float hit = 1.0;
	float t = 0.02;

	for (int i = 0; i < 1000; i++) {
		float h = scene(origin + direction * t).x;
		if (h < 0.001) return 0.0;
		t += h;
		hit = min(hit, 10.0 * h / t);
		if (t >= 2.5) break;
	}

	return clamp(hit, 0.0, 1.0);
}

vec2 traceRay( in vec3 origin, in vec3 direction) {
	float material = -1.0;

	float t = 0.02;

	for (int i = 0; i < 100; i++) {
		vec2 hit = scene(origin + direction * t);
		if (hit.x < 0.00005|| t > 20.0) break;
		t += hit.x;
		material = hit.y;
	}

	if (t > 15.0) {
		material = -1.0;
	}

	return vec2(t, material);
}





float calcAO( in vec3 pos, in vec3 nor) {
	float occ = 0.0;
	float sca = 1.0;
	for (int i = 0; i < 9; i++) {
		float hr = 0.01 + 0.12 * float(i) / 4.0;
		vec3 aopos = nor * hr + pos;
		float dd = scene(aopos).x;
		occ += -(dd - hr) * sca;
		sca *= 0.95;
	}
	return clamp(1.0 - 2.0 * occ, 0.0, 1.0);
}
