	for (int m = 0; m < AA; m++)
		for (int n = 0; n < AA; n++) {
			// pixel coordinates
			vec2 o = vec2(float(m), float(n)) / float(AA) - 0.5;
			vec2 p = (-iResolution.xy + 2.0 * (fragCoord + o)) / iResolution.y;
 # else
			vec2 p = (-iResolution.xy + 2.0 * fragCoord) / iResolution.y;
 # endif
			 // camera	
			vec3 ta = vec3(0, 0, 0);

			vec3 ro = vec3(0, cos(mo.y), sin(mo.y));
			vec4 horz = BuildQuat(vec3(0, 1, 0), -mo.x * 2. + iTime * 0.1);
			ro = ta + Rotate(horz, ro) * 4.;

			 // camera-to-world transformation
			mat3 ca = setCamera(ro, ta, 0.0);
			 // ray direction
			vec3 rd = ca * normalize(vec3(p.xy, 2.5));
			 // render
			float distance;
			vec3 col = render(ro, rd, distance);
			 // gamma
			col = pow(col, vec3(0.4545));
			tot += col;
 # if AA > 1
		}
	tot /= float(AA * AA);
 #	endif
	fragColor = vec4(tot, 1.0);
}

