float noise( in vec2 p )
{
    vec2 i = floor( p );
    vec2 f = fract( p );

	vec2 u = f*f*(3.0-2.0*f);

    return mix( mix( hash12( i + vec2(0.0,0.0) ), 
                     hash12( i + vec2(1.0,0.0) ), u.x),
                mix( hash12( i + vec2(0.0,1.0) ), 
                     hash12( i + vec2(1.0,1.0) ), u.x), u.y);
}
float noise13( in vec3 p )
{
    vec3 i = floor( p );
    vec3 f = fract( p );

	vec3 u = f*f*(3.0-2.0*f);

    return mix( 
        mix(
                mix( hash13( i + vec3(0.0,0.0,0.0) ), 
                     hash13( i + vec3(1.0,0.0,0.0) ), u.x),
                mix( hash13( i + vec3(0.0,1.0,0.0) ), 
                     hash13( i + vec3(1.0,1.0,0.0) ), u.x)
               , u.y),
              mix(
                mix( hash13( i + vec3(0.0,0.0,1.0) ), 
                     hash13( i + vec3(1.0,0.0,1.0) ), u.x),
                mix( hash13( i + vec3(0.0,1.0,1.0) ), 
                     hash13( i + vec3(1.0,1.0,1.0) ), u.x)
               , u.y)

         , u.z)
        ;
}

vec3 noise33(  vec3 p )
{
    p=p+10000.;
    vec3 i = floor( p );
    vec3 f = fract( p );

	vec3 u;
    u.x = f.x*f.x*(3.0-2.0*f.x);
    u.y = f.y*f.y*(3.0-2.0*f.y);
    u.z = f.z*f.z*(3.0-2.0*f.z);

    return mix( 
        mix(
                mix( hash33( i + vec3(0.0,0.0,0.0) ), 
                     hash33( i + vec3(1.0,0.0,0.0) ), u.x),
                mix( hash33( i + vec3(0.0,1.0,0.0) ), 
                     hash33( i + vec3(1.0,1.0,0.0) ), u.x)
               , u.y),
              mix(
                mix( hash33( i + vec3(0.0,0.0,1.0) ), 
                     hash33( i + vec3(1.0,0.0,1.0) ), u.x),
                mix( hash33( i + vec3(0.0,1.0,1.0) ), 
                     hash33( i + vec3(1.0,1.0,1.0) ), u.x)
               , u.y)

         , u.z)
        ;
}

float fbm( vec2 p )
{
      mat2 m = mat2( 0.80,  0.60, -0.60,  0.80 );

    float f = 0.0;
    f += 0.5000*noise( p ); p = m*p*2.02;
    f += 0.2500*noise( p ); p = m*p*2.03;
    f += 0.1250*noise( p ); p = m*p*2.01;
    f += 0.0625*noise( p );
    return f/0.9375;
}

vec3 fbm33( vec3 p )
{

     mat3 m = mat3(vec3(0.80,  0.60, -0.6).xyz,
                    vec3(0.80,  0.60, -0.6).yzx,
                    vec3(0.80,  0.60, -0.6).zxy);
    vec3 f = vec3(0.0);
    f += 0.5000*noise33( p ); p = m*p*2.02;
    f += 0.2500*noise33( p ); p = m*p*2.03;
    f += 0.1250*noise33( p ); p = m*p*2.01;
    f += 0.0625*noise33( p );
    return f/0.9375;
}

float fbm13( vec3 p )
{
         float m =1.;


    float f =  (0.0);
    f += 0.5000*noise13( p ); p = m*p*2.02;
    f += 0.2500*noise13( p ); p = m*p*2.03;
    f += 0.1250*noise13( p ); p = m*p*2.01;
    f += 0.0625*noise13( p );
    return f/0.9375;
}

vec2 fbm2( in vec2 p )
{
    return vec2( fbm(p.xy), fbm(p.yx) );
}


vec3 fbm33Grad( in vec3 position) {
	vec3 epsilon = vec3(0.001, 0.0, 0.0);
	vec3 n = vec3(
		fbm13(position + epsilon.xyy)  - fbm13(position - epsilon.xyy) ,
		fbm13(position + epsilon.yxy) - fbm13(position - epsilon.yxy) ,
		fbm13(position + epsilon.yyx) - fbm13(position - epsilon.yyx) );
	return normalize(n);
}



