float   arandom (float aseed) {return mod( mod(aseed*43758.5453123, 1.9)+aseed* 78.233 ,1.0);}

float value_at (float x,float y,float z) {return arandom(arandom(arandom(x)+y)+z);}


float noise3d(vec3 p) {

    // single octave xyz noise
	  float  px= floor (p.x); 	   float    py= floor (p.y); 	   float    pz= floor (p.z);
  		float	lbx= smoothstep(0.0,1.0,p.x-px);	
  		float  lby= smoothstep(0.0,1.0,p.y-py);	 
 		 float  lbz= smoothstep(0.0,1.0,p.z-pz);
	  	  float    p0 = value_at (px,  py,  pz);    float    p1 = value_at (px+1.0,py,  pz);
      	  float    p2 = value_at (px+1.0,py+1.0,pz);    float    p3 = value_at (px,  py+1.0,pz);		
      	  float    p4 = value_at (px,  py,  pz+1.0);  float      p5 = value_at (px+1.0,py,  pz+1.0);
      	  float    p6 = value_at (px+1.0,py+1.0,pz+1.0);  float      p7 = value_at (px,  py+1.0,pz+1.0);   	     
          float  p01 =mix(p0,p1,lbx);			  float  p32 =mix(p3,p2,lbx);
    	  float  	p45 =mix(p4,p5,lbx);		  float  	p76 =mix(p7,p6,lbx);
		  float  	p0132=mix(p01,p32,lby);		  float  	p4576=mix(p45,p76,lby);  	 
    float   c1=mix(p0132,p4576,lbz);
    return    (c1);}


float  synmin(float a,float  b,float  r ,float w,float g) {
    // drop-in function for smin  

        float  e =   max(r*0.02,(abs(a-b)/r) );
        return min (a,b)- max(0.01,(r*e*w*(exp(1.0-(e*g ))))/max(a,b)*0.5)

            ;}

float conecapsule2 (vec3 p, vec4 p1 ,vec4 p2  ) {
// eats vec4 (x,y,z,r) X 2
vec3 a=mix(p1.xyz,p2.xyz,0.15); // pull back a little from joint
vec3 b=mix(p1.xyz,p2.xyz,0.9);
float		d1=p1.w;
float		d2=p2.w*0.9;
vec3		ba = b - a;
vec3		pa = p - a;
    float v= dot(pa,ba) / dot(ba,ba);
float		h = clamp(  v,   0.0,   1.0);
float			d=mix(d1,d2,  smoothstep(0.0,1.0,   h));
     float r= d ;// / 2.0;

            return length (pa - ba*h) - r;

}

 float conecapsule (vec3 p, vec4 p1 ,vec4 p2 ,float w, float z) {

    // wrapper of conecapsule to introduce some bump noise

     vec3 n=normalize(vec3 (
     conecapsule2(p,p1,p2)-conecapsule2(p+vec3(0.01,0.0,0.0) ,p1,p2),
       conecapsule2(p,p1,p2)-conecapsule2(p+vec3(0.0,0.01,0.0),p1,p2 ),
      conecapsule2(p,p1,p2)-conecapsule2(p+vec3(0.0,0.0,0.01),p1,p2 )
           ));


float bumpb=noise3d(n*w+p/(45./w))*z;//mostly normaldriven with a dash of global p

return conecapsule2 (p,p1,p2) +bumpb 
   ; 
}


