vec3 spread31(float i,float seed){
return vec3(sin(i*PI)*cos(seed*97.+i*TAU*7.4214),sin(i*PI),sin(seed*97.+i*TAU*7.4214));
}

float fractalSphere(vec3 pos, float r) {
	int subs = 4;
	int count = 50;
	vec3 c, C, move;
	float d, R,l;
	R = r;
	if (sdBB(pos,r) < 0.25) {
		d = sdBB(pos,0.);
		for (int i = 0; i < count; i++) {
			r = R;
			C = vec3(0.);
			for (int j = 0; j < subs; j++) {

				r *= .5;
				move = (hash33(C+vec3(i))-0.5)*1.157;
				C = C +  (move) * r;
                   l=sdBB(pos-C,r);
                if (l > 0.2) break;
			}
			d = min(d, sdBB(pos - C, r ) ) ;
            if (d<0.)break;
		}
		 d=max(d,sdBB(pos,R) );
	} else {
		d = sdBB(pos,r);
	}
	return d;
}


