vec4 map(vec3 p) {
    float plane = abs(p.y + 0.9);

float r= hash1(floor(p.xz/tile))*0.5+0.5;    
float g= hash1(vec2(floor(p.xz/tile)+2.));    
float b= hash1(vec2(floor(p.z/tile)))*0.25;    



    float w = shape1(p.xz,r);
    float d = shape2(vec2(w, p.y),g);


    return (p.y<-0.8 ?vec4(d,grass(  p.xz*0.04)):vec4(d, vec3(r,g,b)/ max(1.,4.*(p.y-2.2)) ));
}

float calcShadow(vec3 ro, vec3 rd, float mint, float maxt) {
    float t = mint;
    float res = 1.0;
    for (int i = 0; i < 100; i++) {
        float h = map(ro + rd * t).x;
        res = min(res, 1.1 * h / t);
        t += h;
        if ((h < 0.001) || (t > maxt)) {
            break;
        }
    }
    return clamp(res, 0.0, 1.0);
}

vec4 trace(vec3 ro, vec3 rd) {
    const float kTMin = 0.001;
    const float kTMax =300.0;
    const float kEps = 0.0001;

    float t = kTMin;
    vec4 res;
    for (int i = 0; i < 370; i++) {
        vec3 pos = ro + rd * t;
        res = map(pos);
        if ((res.x < kEps) || (t > kTMax)) {
            break;
        }
        t += res.x * 0.25;
    }

    if (t < kTMax) {
        return vec4(t, res.yzw);
    } else {
        return vec4(-1.0,0,0,0);
    }
}

vec3 calcNormal(vec3 p) {
    const vec2 e = vec2(0.005, 0);
    float dp = map(p).x;
    return normalize(vec3(dp - map(p - e.xyy).x,
        dp - map(p - e.yxy).x,
        dp - map(p - e.yyx).x));
}

mat3 calcCamera(vec3 eye, vec3 target) {
    vec3 cw = normalize(target - eye);
    vec3 cu = cross(cw, vec3(0, 1, 0));
    vec3 cv = cross(cu, cw);
    return mat3(cu, cv, cw);
}

vec3 cameraPath( float t )

{
    t=t*4.;
    vec2 p  = vec2(400.0*sin(t/102.),400.*cos(t/103.));
 	p  += vec2(40.0*sin(t/12.),60.*cos(t/13.));
    float z = 18.0+1.*sin(t/02.) ;

	return vec3( p.x,z , p.y );
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {


    vec2 mo = vec2(0.95, -0.2);
    if (iMouse.z > 0.5) {
        mo = 10.0 * iMouse.xy / iResolution.xy - 1.0;
        mo *= 3.14159 * vec2(0.4, 0.1);
    }
    mo += 3.14159 * 0.15 * iTime;

    vec3 eye = cameraPath(iTime);
    vec3 target = cameraPath(iTime+23.+sin(iTime)*6.)-vec3(0,30,0);

    mat3 cam = calcCamera(eye, target);

    vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    vec3 rd = cam * normalize(vec3(uv, 2.0));

    vec3 col = vec3(0.60);

    vec4 res = trace(eye, rd);
    if (res.x > 0.0) {
        vec3 pos = eye + rd * res.x;
        vec3 nor = calcNormal(pos);
        vec3 ldir = normalize(vec3(-10.5, 20.8, 24.0));

        if (res.y < 1.5) {
                        col = col* 0.4 + 0.6 * (vec3(1) - nor.zxy);
            col = col*0.2 + 0.8 * (vec3(res.yzw) );

            float dif = abs(dot(nor, ldir));
            vec3 ref = reflect(rd, nor);
            float spe = pow(clamp(dot(ref, ldir), 0.0, 1.0), 2.0);

            col *= (0.2 + 0.8 * dif);
            float edge = pow(1.0 - dot(-rd, nor), 1.011);
            col += 0.1 * edge + spe;
        }

        float sh = calcShadow(pos, ldir, 0.1, 30.0);
        col *= (0.5 + sh);
    }



    if (fract((iTime + 1.) / 10.) < 0.165) {
        // col=vec3( shape1(uv*20.-vec2(0,4))>0. )

        float len = shape1(eye.xz+uv * 50. - vec2(0, 4), hash1(floor((eye.xz+uv * 50. - vec2(0, 4))/tile))) / 10.;

        //distance field coloring by iq https://www.shadertoy.com/view/4lcBWn
        col = vec3(1.0) - sign(len) * vec3(0.1, 0.4, 0.7);
        col *= 1.0 - exp(-iResolution.y * 0.008 * abs(len));
        col *= 0.8 + 0.2 * cos(iResolution.y * 0.3 * abs(len));
        col = mix(col, vec3(1.0), max(1.0 - abs(len) * iResolution.y * 0.2, 0.0));
        fragColor = vec4(col * col, 1.0);
    }
    if (fract((iTime + 1.) / 10.) < 0.08) {
        // col=vec3( shape2(uv*20.-vec2(0,-8))>0. );

        float len = shape2(uv * 20. - vec2(0, -2), hash1(floor((uv * 20. - vec2(0, -2))/tile))) / 10.;

        //distance field coloring by iq https://www.shadertoy.com/view/4lcBWn
        col = vec3(1.0) - sign(len) * vec3(0.1, 0.4, 0.7);
        col *= 1.0 - exp(-iResolution.y * 0.008 * abs(len));
        col *= 0.8 + 0.2 * cos(iResolution.y * 0.3 * abs(len));
        col = mix(col, vec3(1.0), max(1.0 - abs(len) * iResolution.y * 0.2, 0.0));
        if (abs(uv.x) < 0.005) col = vec3(1.);

        fragColor = vec4(col * col, 1.0);
    }
    fragColor = vec4(col, 1.0);
}

