// Spherical Fibonnacci points, as described by Benjamin Keinert, Matthias Innmann, 
// Michael Sanger and Marc Stamminger in their paper (below)


//=================================================================================================
// https://dokumen.tips/documents/spherical-fibonacci-mapping-fibonacci-mapping-benjamin-keinert-1matthias-innmann.html
//=================================================================================================
const float PI  = 3.14159265359;
const float PHI = 1.61803398875;


vec3 inverseSF( vec3 p, float n ) 
{
    float m = 1.0 - 1.0/n;
    float U;
    float phi = min(atan(p.y, p.x), PI), cosTheta = p.z;

    float k  = max(2.0, floor( log(n * PI * sqrt(5.0) * (1.0 - cosTheta*cosTheta))/ log(PHI+1.0)));
    float Fk = pow(PHI, k)/sqrt(5.0);
    vec2  F  = vec2( round(Fk), round(Fk * PHI) ); // k, k+1

    vec2 ka = 2.0*F/n;
    vec2 kb = 2.0*PI*( fract((F+1.0)*PHI) - (PHI-1.0) );    

    mat2 iB = mat2( ka.y, -ka.x, 
                    kb.y, -kb.x ) / (ka.y*kb.x - ka.x*kb.y);

    vec2 c = floor( iB * vec2(phi, cosTheta - m));
    vec2 ca =  ( iB * vec2(phi, cosTheta - m));
    float d = 8.0;
    float j = 0.0;
     vec2 uv ;
    vec3 q;
    for( int s=0; s<4; s++ ) 
    {
          uv = vec2( float(s-2*(s/2)), float(s/2) );

        float i = dot(F, uv + c); // all quantities are ingeters (can take a round() for extra safety)
        float phi = 2.0*PI*fract(i*PHI);
        float cosTheta = m - 2.0*i/n;
        float sinTheta = sqrt(1.0 - cosTheta*cosTheta);

          q = vec3( cos(phi)*sinTheta, sin(phi)*sinTheta, cosTheta );

        float squaredDistance = dot(q-p, q-p);
        if (squaredDistance < d) 
        {
            d = squaredDistance;
            j = i;
        }
    }


     uv = vec2( float(2-2*(2/2)), float(2/2) );

        float  i = dot(F, uv + c); // all quantities are ingeters (can take a round() for extra safety)
          phi = 2.0*PI*fract(i*PHI);
          cosTheta = m - 2.0*i/n;
         float sinTheta = sqrt(1.0 - cosTheta*cosTheta);

        vec3 qc = vec3( cos(phi)*sinTheta, sin(phi)*sinTheta, cosTheta );
     uv = vec2( float(0-2*(0/2)), float(0/2) );

          i = dot(F, uv + c); // all quantities are ingeters (can take a round() for extra safety)
          phi = 2.0*PI*fract(i*PHI);
          cosTheta = m - 2.0*i/n;
          sinTheta = sqrt(1.0 - cosTheta*cosTheta);

          q = vec3( cos(phi)*sinTheta, sin(phi)*sinTheta, cosTheta );

        U=   1.+(dot(normalize(p-qc),normalize(p-q)));

    return vec3( j, sqrt(d),U );
}


