vec2 opSmoothUnion2d( vec2 d1, vec2 d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2.y-d1.y)/k, 0.0, 1.0 );
    float hx = clamp( 0.5 + 0.5*(d2.y-d1.y)/(k*1.4), 0.0, 1.0 );
    return vec2(
       mix( d2.x, d1.x, hx ) - (k*1.4)*hx*(1.0-hx) ,
	    // d2.y< d1.y?d2.x:d1.x,
        mix( d2.y, d1.y, h ) - k*h*(1.0-h) );
}

vec2 sdBox2d( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return vec2( min(abs(p.x),abs(p.y))*max(b.x,b.y), length(max(d,vec2(0))) + min(max(d.x,d.y),0.0));
}
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0);
}
vec2 sdCircle2d( vec2 p, float r )
{
  return vec2(atan(p.y,p.x)/6.28318, length(p) - r);
} 

vec2 sdCircle2dCC( vec2 p, float r )
{
    float a=atan(p.y,p.x)*r;
  //  a=abs(a*2.-1.);

  return vec2(a, length(p) - r);
} 


vec2 sdCircle2dCW( vec2 p, float r )
{
    float a=atan(p.y,p.x)*r;
//a=abs(a*2.-1.);
  return vec2( a , length(p) - r);
} 

vec2 sdBezier2d( in vec2 pos, in vec2 A, in vec2 B, in vec2 C )
{    
    vec2 a = B - A;
    vec2 b = A - 2.0*B + C;
    vec2 c = a * 2.0;
    vec2 d = A - pos;
    float kk = 1.0 / dot(b,b);
    float kx = kk * dot(a,b);
    float ky = kk * (2.0*dot(a,a)+dot(d,b)) / 3.0;
    float kz = kk * dot(d,a);      
    float res = 0.0;
    float p = ky - kx*kx;
    float p3 = p*p*p;
    float q = kx*(2.0*kx*kx - 3.0*ky) + kz;
    float h = q*q + 4.0*p3;
    float t;
    float t2;
    float bezlength= (length(A-B)+length(B-C)+length(A-C))*.35 ;
    if( h >= 0.0) 
    { 
        h = sqrt(h);
        vec2 x = (vec2(h, -h) - q) / 2.0;
        vec2 uv = sign(x)*pow(abs(x), vec2(1.0/3.0));
          t = uv.x + uv.y - kx;
          t2 = uv.x + uv.y - kx;
        t = clamp( t, 0.0, 1.0 );
        vec2 qos = d + (c + b*t)*t;
        res = dot(qos,qos);
    }
    else
    {
        float z = sqrt(-p);
        float v = acos( q/(p*z*2.0) ) / 3.0;
        float m = cos(v);
        float n = sin(v)*1.732050808;
        vec3 t = vec3(m + m, -n - m, n - m) * z - kx;
        vec3 tc =t;
        t2=tc.x;
 		t = clamp( t, 0.0, 1.0 );
		vec2 qos = d + (c + b*t.x)*t.x;
        res = dot(qos,qos);
        qos = d + (c + b*t.y)*t.y;
        t2=(res>dot(qos,qos))?tc.y:t2 ;
        res = min(res,dot(qos,qos));
        qos = d + (c + b*t.z)*t.z; 
        t2=(res>dot(qos,qos))?tc.z:t2 ;
        res = min(res,dot(qos,qos));
    }
    t2-=0.5;
    return vec2( t2*bezlength,sqrt( res ));
}
float ndot(vec2 a, vec2 b ) { return a.x*b.x - a.y*b.y; }
float sdRhombus( in vec2 p, in vec2 b ) 
{
    vec2 q = abs(p);
    float h = clamp((-2.0*ndot(q,b)+ndot(b,b))/dot(b,b),-1.0,1.0);
    float d = length( q - 0.5*b*vec2(1.0-h,1.0+h) );
    return d * sign( q.x*b.y + q.y*b.x - b.x*b.y );
}

float sdVesica(vec2 p, float r, float d)
{
    p = abs(p);

    float b = sqrt(r*r-d*d);  // can delay this sqrt by rewriting the comparison
    return ((p.y-b)*d > p.x*b) ? length(p-vec2(0.0,b))
                               : length(p-vec2(-d,0.0))-r;
}
vec2 min2d(vec2 a, vec2 b){
return a.y<b.y ?a:b;
}

