void layer (out vec4 col, in vec3 uv, in float zoom, in float music)
{

    uv.xy *= mix(1.1, 0.01, zoom);

    float deCenter = length(uv);

    deCenter = min(1., deCenter * deCenter);

    vec2 smokeDin = vec2(-iTime + sin(uv.z*23.)*12.34, iTime + cos(uv.z*11.))*0.02;

    vec2 smokeUV = uv.xy;

    float texC = texture(iChannel3, smokeUV*13. * sin(music*0.1 + uv.x) + smokeDin.yx*4.5).r
        		*texture(iChannel3, smokeUV*15.*zoom*uv.y - smokeDin.yx*3.2).r*0.015;//* deCenter ;


    float texA = (1.- texture(iChannel3, smokeUV*0.6 - texC * 0.5 + smokeDin*0.2).r) ;

    texA*=texA;

    float texB = 1.-texture(iChannel3, smokeUV *  (1.-texA*0.1 + texC * 0.4 * (.5-texA)) + smokeDin*0.1).r;// * deCenter;


    texA *= (0.5 + texB)*0.25;

    texA *= texA * 8.* zoom * zoom * (1.-col.a) * deCenter;


    vec2 grid = uv.xy*10.;

     uv.xy = mod(grid,1.0);

     grid -= uv.xy;

    uv.xy-=0.5;


    vec4 add = vec4(0,0,0, texA);

    float depthMod = uv.z*0.123;

    float iTimeAndZoom = iTime + zoom;

    float cutMod = col.a * (64.) * music;

    float distMod =  (0.1 + texA*10.  + cutMod) * max(0.1,  zoom - col.a*4.) ;

    for(int x=-1;x<=1;x++){
    	for(int y=-1;y<=1;y++){

            vec2 dUv = uv.xy - vec2(x,y);
            vec2 dGrid = grid + vec2(x,y);

            vec3 vol = texture(iChannel2, vec3(dGrid.xy*0.1+depthMod, (dGrid.x + iTimeAndZoom)*0.001 )).rgb ;

            float big = vol.b*vol.b;

            dUv += (vol.xy - .5)*1.5;


            float len = length(dUv);

            float dist = big * distMod/len; 

            float cut = smoothstep(1.-col.a*0.5,.5,len);

            float ray = max(0., 1.-abs(dUv.x*dUv.y*300.))* cut * music * big;


            dist += ray * ray * 0.2;

            add.rgb +=  dist * cut * vol;
    	}
     }



   col += add * zoom * (1.-zoom);


}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

     vec3 noise =  texture(iChannel2, vec3(uv*123.123+iTime, iTime+uv.x*uv.y*543.21)).rgb;

    vec4 col = vec4(0,0,0,0);

     uv -= 0.5;

    vec2 M = vec2(sin(iTime*.1)*3.,cos(iTime*.143)*2.);

    uv += M;


    float aspect = iResolution.x/iResolution.y; 

    uv.x*=aspect;


    const float stepCnt = 5.;
    const int steCntInt = 5;
    const float oneStep = 1./stepCnt;

    float zoom = iTime*0.1;



    float index = floor(mod(zoom, 1.)*stepCnt);

    zoom = mod(zoom, oneStep);

    float off = 0.;

    float music = 0.;  

    for(int i=0;i<steCntInt;i++){

        float totalZoom = zoom + off;

        music = texture(iChannel0, vec2(mix(0.01, 0.8, totalZoom),0.25)).x;

        // music *= music * 2.;

        music = smoothstep(0.,.2,music);

    	layer(col, vec3(uv.xy,index), totalZoom, music);
    	off += oneStep;
        index = mod(index-1., stepCnt);

    }    

   col.rgb*=vec3(0.5,0.9,1) * 5. * oneStep;

    col.rgb += noise*col.rgb*0.15;

    fragColor = vec4(col.rgb,1.0);
}

