//----------------------------------------------------------------------------------------
// Quaternios
//----------------------------------------------------------------------------------------
 vec4 NormQuat(vec4 q)
{
    float lenSQ = dot(q, q);
    float invLenSQ = 1./lenSQ;
    return q*invLenSQ;
}

vec4 BuildQuat(vec3 axis, float angle)
{
    angle *= 0.5;
    float s = sin(angle);
    return NormQuat(vec4(axis*s, cos(angle)));
}

vec4 MulQuat(vec4 q1, vec4 q2)
{
    vec4 res = vec4(0.);
    res.w   = q1.w*q2.w - dot(q1.xyz, q2.xyz);
    res.xyz = q1.w*q2.xyz + q2.w*q1.xyz + cross(q1.xyz, q2.xyz);
    return NormQuat(res);
}



vec3 Rotate(vec4 q, vec3 v)
{
    vec3 t = 2.*cross(q.xyz, v);
    return v + q.w*t + cross(q.xyz, t);
}


float fromTo(vec3 p,vec3 q)
{
return acos(dot(p,q));

}
vec4 quatLookAt(vec3 forward,vec3 up){
    forward=normalize(forward);
    up=normalize(up);
return  (BuildQuat(cross(forward,up),fromTo(forward,up)));
}


vec4 quatInv(in vec4 q)
{
    return vec4(-q.xyz, q.w);
}

//https://en.wikipedia.org/wiki/Slerp#Quaternion_Slerp

vec4 slerp(vec4 v0, vec4 v1, float t) {
    // Only unit quaternions are valid rotations.
    // Normalize to avoid undefined behavior.
     NormQuat(v0);
     NormQuat(v1);

    // Compute the cosine of the angle between the two vectors.
    float dotp = dot(v0, v1);

    // If the dot product is negative, slerp won't take
    // the shorter path. Note that v1 and -v1 are equivalent when
    // the negation is applied to all four components. Fix by 
    // reversing one quaternion.
    if (dotp < 0.0f) {
        v1 = -v1;
        dotp = -dotp;
    }  

      float DOT_THRESHOLD = 0.9995;
    if (dotp > DOT_THRESHOLD) {
        // If the inputs are too close for comfort, linearly interpolate
        // and normalize the result.

        vec4 result = v0 + t*(v1 - v0);
        NormQuat(result);
        return result;
    }

    // Since dot is in range [0, DOT_THRESHOLD], acos is safe
    float theta_0 = acos(dotp);        // theta_0 = angle between input vectors
    float theta = theta_0*t;          // theta = angle between v0 and result
    float sin_theta = sin(theta);     // compute this value only once
    float sin_theta_0 = sin(theta_0); // compute this value only once

    float s0 = cos(theta) - dotp * sin_theta / sin_theta_0;  // == sin(theta_0 - theta) / sin(theta_0)
    float s1 = sin_theta / sin_theta_0;

    return (s0 * v0) + (s1 * v1);
}

