        mod (floor(pm2.z/10.),2.)
        ,2.);

 float c3= mod( mod (floor(p.x/10.),2.)+mod (floor(p.y/10.),2.),2.);

 float c= sphere2>sphere1?c1:c2;
       c= plane> min(sphere2,sphere1)?c:c3;

