    seed.x = floor(sin(seed.x * 51024.0) * 3104.0);
    seed.y = floor(sin(seed.y * 1324.0) * 554.0);

    uv = uv * 2.0 - 1.0;
    pR(uv, seed.x + seed.y);
    uv += sin(seed.x - seed.y)*.25;
    //uv =  (clamp(uv * 0.5 + 0.5, 0.0, 1.0) )  ;
        uv+= (vec2(snoise(uv*3.),snoise(2.*uv+100.))-0.25)*0.2;



    float x = sign(0.03 - abs(length(uv) - snoise(seed )*0.9 ));
    return clamp(   x, 0.0, 1.0);
}

float layer(vec2 uv, vec2 frequency, vec2 offset, float angle)
{
    pR(uv, angle);
    uv = uv * frequency + offset;
    return scratch(fract(uv), floor(uv));
}

float scratches(vec2 uv)
{
    uv *= uScale;
    uv += uOffset;
    vec2 frequency = uBaseFrequency;
    float scratches = 0.0;
    for(int i = 0; i < uLayers; ++i)
    {
        float fi = float(i);
    	scratches  =max(scratches, layer(uv, frequency, vec2(fi, fi), fi * 3145.0));
        frequency += uFrequencyStep;
    }
    return  ( scratches);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;

    // using AA by Shane:
    // https://www.shadertoy.com/view/4d3SWf
    const float AA = 4.; // Antialias level. Set to 1 for a standard, aliased scene.
    const int AA2 = int(AA*AA);
    float col = 0.0;
    vec2 pix = 2.0/iResolution.yy/AA; // or iResolution.xy
    for (int i=0; i<AA2; i++){ 

        float k = float(i);
        vec2 uvOffs = uv + vec2(floor(k/AA), mod(k, AA)) * pix;
        col += scratches(uvOffs);
    }
    col /= (AA*AA);

	fragColor = vec4(col);
}

vec3 mod289(vec3 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec2 mod289(vec2 x) {
  return x - floor(x * (1.0 / 289.0)) * 289.0;
}

vec3 permute(vec3 x) {
  return mod289(((x*34.0)+1.0)*x);
}

float snoise(vec2 v)
  {
  const vec4 C = vec4(0.211324865405187,  // (3.0-sqrt(3.0))/6.0
                      0.366025403784439,  // 0.5*(sqrt(3.0)-1.0)
                     -0.577350269189626,  // -1.0 + 2.0 * C.x
                      0.024390243902439); // 1.0 / 41.0
// First corner
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);

// Other corners
  vec2 i1;
  //i1.x = step( x0.y, x0.x ); // x0.x > x0.y ? 1.0 : 0.0
  //i1.y = 1.0 - i1.x;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  // x0 = x0 - 0.0 + 0.0 * C.xx ;
  // x1 = x0 - i1 + 1.0 * C.xx ;
  // x2 = x0 - 1.0 + 2.0 * C.xx ;
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;

// Permutations
  i = mod289(i); // Avoid truncation effects in permutation
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
		+ i.x + vec3(0.0, i1.x, 1.0 ));

  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy), dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;

// Gradients: 41 points uniformly over a line, mapped onto a diamond.
// The ring size 17*17 = 289 is close to a multiple of 41 (41*7 = 287)

  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;

// Normalise gradients implicitly by scaling m
// Approximation of: m *= inversesqrt( a0*a0 + h*h );
  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );

// Compute final noise value at P
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}

void pR(inout vec2 p, float a)
{
    float sa = sin(a);
    float ca = cos(a);
    p *= mat2(ca, sa, -sa, ca);
}
vec2 pRr( vec2 p, float a)
{
    float sa = sin(a);
    float ca = cos(a);
    p *= mat2(ca, sa, -sa, ca);
    return p;
}

