float sdArc( in vec2 p, in vec2 sca, in vec2 scb, in float ra, float rb )

{
    p=fract( p) -0.5;

    p *= mat2(sca.x,sca.y,-sca.y,sca.x);
    p.x = abs(p.x);
    float k = (scb.y*p.x>scb.x*p.y) ? dot(p.xy,scb) : length(p.xy);
    return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}


