// Tileable Cells. By David Hoskins. 2013.

#define NUM_CELLS	8.0	// Needs to be a multiple of TILES!
#define TILES 		1.0		// Normally set to 1.0 for a creating a tileable texture.

//#define SHOW_TILING			// Display yellow lines at tiling locations.
#define ANIMATE			// Basic movement using texture values.

//------------------------------------------------------------------------
vec2 Hash2(vec2 p)
{
	#ifdef ANIMATE

	float t = fract(.0003);
	return texture(iChannel0, p*vec2(.135+t, .2325-t), -100.0).xy;

	#else

	float r = 523.0*sin(dot(p, vec2(53.3158, 43.6143)));
	return vec2(fract(15.32354 * r), fract(17.25865 * r));

	#endif
}

//------------------------------------------------------------------------
float Cells(in vec2 p, in float numCells)
{
	p *= numCells;
	float d = 1.0e10;
			vec2 tp = floor(p) ;
			tp = fract(p )- Hash2(tp)*0.8 ;
			d = length(tp);
	return  (d);
	//return 1.0 - d;// ...Bubbles.
}

//------------------------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	#ifdef ANIMATE
	float c = Cells(uv+iTime*.025, NUM_CELLS);
	#else
	float c = Cells(uv, NUM_CELLS);
	#endif

	vec3 col = round(vec3(1.-((1.-c)*(1.-c)*(1.-c)*(1.-c)*(1.-c)*(1.-c)*(1.-c))));

	#ifdef SHOW_TILING
	// Flash tile borders...
	vec2 pixel = TILES / iResolution.xy;
	uv *= TILES;

	float f = floor(mod(iTime*.5, 2.0)); 	// Flash value.
	vec2 first = step(pixel, uv) * f;		   	// Rule out first screen pixels and flash.
	uv  = step(fract(uv), pixel);				// Add one line of pixels per tile.
	col = mix(col, vec3(1.0, 1.0, 0.0), (uv.x + uv.y) * first.x * first.y); // Yellow line

	#endif

	fragColor = vec4(col, 1.0);
}

//  1 out, 2 in...
float hash12(vec2 p) {
	vec3 p3 = fract(vec3(p.xyx) * .1031);
	p3 += dot(p3, p3.yzx + 33.33);
	return fract((p3.x + p3.y) * p3.z);
}
///  2 out, 2 in...
vec2 hash22(vec2 p) {
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
	return fract((p3.xx + p3.yz) * p3.zy);

}

vec3 pal( in float t) {
	vec3 pa = vec3(0.5, 0.5, 0.5);
	vec3 pb = vec3(0.5, 0.5, 0.5);
	vec3 pc = vec3(1.0, 1.0, 1.0);
	vec3 pd = vec3(0.3, 0.20, 0.20);
	return pa + pb * cos(6.28318 * (pc * t + pd));
}

vec3 getTileData(vec2 uv) {
	vec2 cornerUV = floor(uv);
	float id = (hash12(cornerUV));
	vec2 tileCore = 0.5 + ((hash22(cornerUV)) - 0.5) * 0.95;
	return vec3(tileCore, id);
}

bool insideBox(vec2 v, vec2 b, vec2 t) {

	return
	v.x < max(t.x, b.x) && v.x > min(t.x, b.x) &&

	v.y < max(t.y, b.y) && v.y > min(t.y, b.y)

	;

}

vec3 tile(vec2 uv) {
	vec2 tileUV = uv - floor(uv);

	vec2 o = vec2(1., 0.);
	vec3 res1 = getTileData(uv + o.yy);
	vec3 res2 = getTileData(uv + o.xy);
	vec3 res3 = getTileData(uv + o.yx);
	vec3 res4 = getTileData(uv + o.xx);
	float id = 0.;

	if (insideBox(tileUV, vec2(0., res1.y), res3.xy + o.yx)) id = res3.z;
	if (insideBox(tileUV, vec2(res3.x, res2.y), res4.xy + o.xx)) id = res4.z;
	if (insideBox(tileUV, vec2(res1.x, 0.), res2.xy + o.xy)) id = res2.z;
	if (insideBox(tileUV, vec2(0.), res1.xy)) id = res1.z;

	vec2 cornerUV = floor(uv);
	float rnd = hash12(cornerUV);
	if (rnd < 0.25 && insideBox(tileUV, vec2(res1.x, 0.), res2.xy + o.xy)) id = res2.z;
	if (rnd < 0.50 && insideBox(tileUV, vec2(res3.x, res2.y), res4.xy + o.xx)) id = res4.z;
	if (rnd < 0.75 && insideBox(tileUV, vec2(0., res1.y), res3.xy + o.yx)) id = res3.z;

	//if (length((res1.xy) - tileUV) < 0.05) id = .5;

	return pal(id);
}

vec3 difftile(vec2 uv) {

return tile(  uv).yxy-tile(  uv+.05).zyz;

}

void pR(inout vec2 p, float a) {
	p = cos(a) * p + sin(a) * vec2(p.y, -p.x);
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	// Normalized pixel coordinates (from 0 to 1)
	vec2 uv = fragCoord / iResolution.x - 0.5;
	pR(uv, iTime);
	// Time varying pixel color
	uv = uv * (cos(iTime) + 1.2) * 30. + vec2(sin(iTime * .6), cos(iTime * .6)) * 17.;
	vec3 col = difftile(uv) * 0.2 + 0.5;
	// Output to screen
	fragColor = vec4(col, 1.0);
}

