const float speed = .125;

float patternFrac( float x )
{
    return fract(speed*x);
}

const int wholeNotes[] = int[](0,2,4,5,7,9,11);

float patternNote( float x )
{
    int noteID = int( 7.0+7.0*sin( floor( speed*x) ) );
    return float( wholeNotes[noteID%7] + 12*(noteID/7) );
}

float patternFreq( float x )
{
    float f = patternNote(x);
    return 55.0*pow(2.0,f/12.0);
}

float tone( in float freq, in float deca, in float time )

{
    freq*=0.53;
    // fm sound
    float y = sin(6.2831*freq*time + 5.0*sin(6.2831*freq*time) );
    // add some harmonics
    //y *= 0.5*(1.0+y*y);
    // attenaute
    y *= exp(-(1.0+freq/20.0)*deca);
    // attack
    y *= clamp(12.0*deca,0.0,1.0);
	return y;    
}

vec2 mainSound( in int samp, float time )
{
    // reverb
    float y = 0.0;
    float a = 0.7;
    float timeoffset= 0.3*(round(0.125*( 
            sin(time*.24)*1.25 + 
            sin(time*.74)*2.5 + 
            sin(time*1.4)*5. +
            sin(time)*10.) ));

    for( int i=0; i<5; i++ )
    {       
        float hime = timeoffset+time
            - 1.4*float(i)/5.0;
        float freq = patternFreq( hime)  ;
        float deca = patternFrac( hime   );
        y += a*tone( freq, deca, hime*2. );


            hime = -timeoffset+ time            - 1.4*float(i)/5.0;
          freq = patternFreq( hime)  ;
          deca = patternFrac( hime   );
        y += a*tone( freq*2., deca*2., hime );

          hime =   1700.+time            - 1.4*float(i*2)/5.0;
          freq = patternFreq( hime*3.)  ;
          deca = patternFrac( hime   );
        y += a*tone( freq*2., deca*.4, hime );

        a *= 0.6;
    } 




    return vec2( y );
}

