float fbm(in vec2 p)
{

    float f = 0.0;
    for (float i = .5; i > 0.; i -= .154)
    {	if (i != .5)
            p *= mat2(.314, -1, 1, .314) * (2.01 + i) / 1.115625;
        float u = floor(p).x + floor(p).y * 257.;
        vec4 v = vec4(u, u + 1., u + 257., u + 257.+ 1.);
        v = fract(fract(1.23456789 * v) * v / .987654321);
        f += (i * mix(mix(v.x, v.y, fract(p.x)), mix(v.z, v.w, fract(p.x)), fract(p.y)));
    }
    return f;
}

