#define tau (2. * acos(-1.))
#define rndp(rng, maxval) ((1315313u % (rng + 1u)) % maxval)


float synth(in float rt, in int chc)
{

    float sn = 1., sna = 1., m0 = 20., m1 = 1., m0vt = 2.;

    int smp = 5;

    if (chc == 2)
    {
        m1 = 2600.;
        smp = 6;
    }

    if (chc == 1)
    {
        m0 = float[](10., 50., 80., 40., 60.)[int((2. * rt)) % 5];
        m0vt = 3.;
    }

    float bss = 1.7;
    for(int n = 0; n < smp; n++)
    {
        sn += (.5 + 2. * sin(m0 * tau * rt));
        sn += (1. - length((sn - bss) - cos(m0 * tau * rt)));

        m0 *= m0vt;

        (mod(5. * rt, 4.) <= 1. && chc == 2) ? 
            (sna += .5 * abs(1. - 2. * sin(m0 * tau * rt)) + (.2 * sin(m1 * tau * rt)),
             sn = .7 * floor(.5 * sna) * pow(4., 1. - 6. * fract(rt)))
             : sn *= min(1., 2. * fract(6. * rt)) * max(0., 1. - fract(6. * rt));
    }

    sn *= min(1., 2. * fract(2. * rt)) * max(0., 1. - fract(2. * rt));

    return sn;
}



vec2 mainSound( in int samp,float otime)
{
float time=otime+4000.;
    vec2 snd = vec2(1), uni = vec2(1), rev = vec2(1);

    float sum = 0., amp = 1.;
    float tick = (mod(time, 22.888) / 60.), bpm = 38.88 * tick;



    float ivr = 0.125;


    	for(float _ii = 0.; (_ii <= 1.); _ii += ivr)
    	{
        	amp = exp2(-_ii * (_ii + .5));
        	uni += vec2(synth(bpm + 1.01 * amp, 2), synth(bpm + 1. * amp, 2));
        	sum += amp;
    	}

        uni /= sum;


    sum = 0.;
    ivr = 0.0606;

    // https://www.shadertoy.com/view/XldcRr
    // by TekF
    for(float _jj = 0.; (_jj <= 1.); _jj += ivr)
    {
        bpm = 7.776 * tick;
        float rand = fract(120. * sin(_jj * 1000.));
        float t2 = (ivr + _jj) + (3. * rand);
        amp = exp2(-_jj * t2);
         (rev += (amp * uni * .5) 
               + vec2(synth(bpm + 1.025 * amp, 1), synth(bpm + 1.01 * amp, 1)));

        sum += amp;
    }

    rev /= sum;

    return .707 * rev;
}

