// srtuss, 2016

float time;

vec2 rotate(vec2 p, float a)
{
	return vec2(p.x * cos(a) - p.y * sin(a), p.x * sin(a) + p.y * cos(a));
}

float hermite(float x)
{
	return x * x * (3.0 - 2.0 * x);
}

// iq's fast 3d noise
float noise3d(in vec3 x)
{
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f * f * (3.0 - 2.0 * f);
	vec2 uv = (p.xy + vec2(37.0, 17.0) * p.z) + f.xy;
	vec2 rg = texture(iChannel1, (uv + 0.5) / 256.0, -100.0).yx;
	return mix(rg.x, rg.y, f.z);
}


// animated 3d fbm
float fbm3a(vec3 p)
{
	vec2 t = vec2(time * 2.8, 0.0);
	return noise3d(p + t.xyy) * 0.5 + noise3d(p * 2.02 - t.xyy) * 0.25 + noise3d(p * 4.01 + t.yxy) * 0.125;
}

// more animated 3d fbm
float fbm3a_(vec3 p)
{
	vec2 t = vec2(time * 2.8, 0.0);
	return noise3d(p + t.xyy) * 0.5 + noise3d(p * 2.02 - t.xyy) * 0.25 + noise3d(p * 4.01 + t.yxy) * 0.125 + noise3d(p * 8.03 + t.yxy) * 0.0625;
}

vec3 sky(vec3 p)
{
	vec3 col;
	float v = 1.0 - abs(fbm3a(p * 4.0) * 2.0 - 1.0);
	float n = fbm3a_(p * 7.0 - 104.042);
	v = mix(v, pow(n, 0.3), 0.5);

	col = vec3(pow(vec3(v), vec3(13.0, 7.0, 6.0))) * 0.8;
	return col;
}




// 3d fbm
float fbm3(vec3 p)
{
	return noise3d(p) * 0.5 + noise3d(p * 2.02) * 0.25 + noise3d(p * 4.01) * 0.125;
}

vec3 stuff(vec2 p)
{
	vec2 fr = fract(p);
	vec2 fl = floor(p);
	fr = fr * fr * (3.0 - 2.0 * fr);
	return texture(iChannel2, (fl + 0.5 + fr) / 256.0).xyz;
}

vec3 stuff_fbm(vec2 p)
{
	return stuff(p) * 0.5 + stuff(p * 2.02) * 0.25 + stuff(p * 4.01) * 0.125;
}

