#define pi2 6.283185307179586476925286766559

float xsine(float x)
{
    return sin(x * pi2);
}
float xnotem(float note)
{
    return pow(2.0, note / 12.0);
}
float xnotef(float note)
{
    return 440.0 * pow(2.0, (note - 69.0) / 12.0);
}
float xvolmul(float vol)
{
    return exp((vol - 1.0) * 4.0);
}

#define xC	0.0
#define xCS	1.0
#define xD	2.0
#define xDS	3.0
#define xE	4.0
#define xF	5.0
#define xFS	6.0
#define xG	7.0
#define xGS	8.0
#define xA	9.0
#define xAS	10.0
#define xB	11.0

float a(float x, float t)
{
    return (xsine(x * 0.5 + t * 1.0) + 1.0) * 0.1;
}

float addsyn(float t, float bf)
{
    float v = 0.0;
    for(int i = 0; i < 32; ++i)
    {
        float harm = float(i + 1);
        float a = texture(iChannel0, vec2(harm / 512.0, t * 0.1)).x;
        float b = texture(iChannel0, vec2(harm / 512.0, t * 0.1 + 0.5)).x;
        //a *= exp(max(10.0 - harm, 0.0) * -0.05);
        v += a * xsine(harm * t * bf + b * 0.01) / harm;
    }
    return sin(v * (1.0 + sin(t * 0.5) * 20.0));
}

vec2 mega(float time, float bf, vec2 vcs)
{
    vec2 v = vec2(0.0, 0.0);
    /*v += xsine(time * bf * xnotem(0.0 + xC));
    v += xsine(time * bf * xnotem(12.0 + xC));
    v += xsine(time * bf * xnotem(24.0 + xE));
    v += xsine(time * bf * xnotem(36.0 + xB));*/

    #ifdef A
    for(int i = 0; i < 32; ++i)
    {
        float tn = float(i);
    	v += vec2(
            addsyn(time * (1.0 + tn * 0.001) + sin(tn * 28971.04124), 50.0),
            addsyn(time * (1.0 + tn * 0.001) + sin(tn * 22971.04124), 50.0));
    }
    #endif
    for(int i = 0; i < 32; ++i)
    {
        float tn = float(i);
        float det = 0.001;//pow(10.0, -1.0 + (sin(time) + 1.0) * -1.0);
        vec2 rnd = vec2(sin(tn * 28971.04124), sin(tn * 22971.04124));// * 0.01;

        float f = bf;
    	v += vec2(
            addsyn(time + rnd.x, f * (1.0 + tn * det)),
            addsyn(time + rnd.y, f * (1.0 + tn * det)));

        f = f * xnotem(16.0);
        v += vec2(
            addsyn(time + rnd.x * 2.0, f * (1.0 + tn * det)),
            addsyn(time + rnd.y * 2.0, f * (1.0 + tn * det))) * 0.9 * vcs.x;

        f = f * xnotem(19.0);
        v += vec2(
            addsyn(time + rnd.x * 2.0, f * (1.0 + tn * det)),
            addsyn(time + rnd.y * 2.0, f * (1.0 + tn * det))) * 0.3 * vcs.y;
    }
    v *= 0.03;
    return v;
}

float tmul = 100.0 / 60.0;

float perc(float t)
{
    float v = 0.0;

    t *= tmul;
    float id = floor(t * 4.0);
    /*t = mod(t, 8.0);
    t = mod(t, 3.0);
    t = mod(t, 2.5);
    t = mod(t, 1.0);*/
    t = mod(t, 0.25);

    t /= tmul;
    //v += xsine(t * 120.0 + xsine(t * 50.0) * 0.7) * exp(max(t, 0.0) * -30.0);
    //v += fract(sin(t * 10000.0) * 182412.41232) * exp(max(0.0, t) * -10.0) * 0.2;
    //v += xsine(t * 60.0) * 0.5 * smoothstep(0.0, 0.05, t) * exp(max(0.0, t) * -2.0);
    //v += xsine(sqrt(t) * 2000.0) * smoothstep(0.0, 0.01, t) * exp(max(0.0, t) * -10.0);

    float f = fract(sin(id * 192.4124) * 287235.023452);
    v += xsine(t * xnotef(80.0 + floor(f * 24.0))) * smoothstep(0.0, 0.05, t) * exp(max(0.0, t) * -4.0);

    return v * 0.4;
}

vec2 echo(float t, float fbTm)
{
    vec2 v = vec2(0.0, 0.0);
    vec2 amp = vec2(1.0, 0.5);
    vec2 toff = vec2(0.01, 0.0);
    for(int i = 0; i < 10; ++i)
    {
        t -= fbTm;
        v += vec2(perc(t - toff.x), perc(t - toff.y)) * amp;
        toff = toff.yx * 1.01;
        amp = amp.yx * 0.5;
    }
    return v;
}

float fdin(float t, float tstart, float tdur)
{
    return clamp((t - tstart) / tdur, 0.0, 1.0);
}

float fdout(float t, float tstart, float tdur)
{
    return 1.0 - clamp((t - tstart) / tdur, 0.0, 1.0);
}

float fx(float t)
{
    float s;
    float vibI = 100.0;
    float vibF = sin(t * 0.8) + 1.5;
    float phs = 440.0 * t - vibI * cos(vibF * t * pi2) / vibF;
    float v = sin(pi2 * phs) * 0.1;
    return v;
}

vec2 mainSound( in int samp, float time )
{
    vec2 mx;
    float tb = time * tmul;
    tb = mod(tb, 32.0);
    mx.x = fdin(tb, 0.0, 4.0) * fdout(tb, 16.0, 4.0);
    tb += 16.0;
    tb = mod(tb, 32.0);
    mx.y = fdin(tb, 0.0, 4.0) * fdout(tb, 16.0, 4.0);

    mx.y *= min(time * 0.1, 1.0);

    vec2 v = vec2(0.0);
    vec2 vcs = vec2(sin(time * 0.2) * 0.5 + 0.5, sin(time * 0.3) * 0.5 + 0.5);

    v = mega(time, 40.0, vcs) * mx.x;
    v += mega(time, 40.0 * xnotem(9.0), vcs) * mx.x;

    v += mega(time, 40.0 * xnotem(-4.0), vcs) * mx.y;
    v += mega(time, 40.0 * xnotem(0.0 + 12.0), vcs) * mx.y;

    //v += mega(time, 33.0) * mx.y;

    //v += echo(time, tmul / 6.0) * 0.5;

    return v;
}

