  float a=     min(length(p.xz)-.15-o,
  sdTorus(p,vec2(.0725+o,0.2)) );  
  d= max(-a,d);
       d= max(d,  p.y-.3) ;  

	return d;

}

float l4(vec3 p){

  float d=    length(p.xz )-max(0.,p.y+.18);
                      d= max(d,  p.y-.17) ;  

	return d;

}

float df_obj(vec3 p)
{

    float d= l1(p); 
 d=min(d,l2(p));
 d=min(d,l3(p));
 d=min(d,l4(p));

	  d= max(d, max( -p.z, -p.z)) ;  

	return d;

}


float df(vec3 p)
{
	return df_obj(p);
}

vec3 nf(vec3 p)
{
	float e = .02;
	float dfp = df(p);
	return vec3(
		(dfp+df(p+vec3(e,.0,.0)))/e,
		(dfp+df(p+vec3(.0,e,.0)))/e,
		(dfp+df(p+vec3(.0,.0,e)))/e);
}

vec3 tex(vec3 d)
{
	return pow(texture(iChannel1,d).xyz*1.4,vec3(1.4));
}

raygo go(raygo  t){

		vec3 pos = t.pos+t.drefr*.05;
		for (int i=0; i<140; i++)
		{
			float dist = df(pos);
			pos+=t.drefr*-dist;
			if (dist>-.0001) break;
		}
		vec3 n = nf(pos);
		vec3 drefl = reflect(t.drefr,-n);
		vec3 drefr = refract(t.drefr,-n,ior);
		float fresnel = dot(-drefr,n);

return raygo(pos,drefl,drefr,n,fresnel);
}



