float sdCross(vec3 p) {
  float da = maxcomp(abs(p.xy));
  float db = maxcomp(abs(p.yz));
  float dc = maxcomp(abs(p.xz));
  return min(da, min(db, dc)) - 1.0;
}


mat2 rotate(float r) {
  float c = cos(r);
  float s = sin(r);
  return mat2(c, s, -s, c);
}

float map(vec3 p) {
  p.zx *= rotate(iTime);
  p.yx *= rotate(iTime * 0.5);



  float d;

    float  distToCapsule =sdCylinder( p, vec2(.350,1.76) );

    d=distToCapsule;


  float s = 1.4;
  for (int m = 0; m < 3; m++) {
    vec3 a = mod(p * s, 2.0) - 1.0;
    s *= 3.0;
    vec3 r = 1.0 - 3.01 * abs(a*1.01);
    float c = sdCross(r) / s;
    d = max(d, c);
  }

      p.zx *= rotate(1.+iTime);
  p.yx *= rotate(iTime * 0.5);



  float d2;

    float  distToCapsule2 =sdCylinder( p, vec2(1.7,.12) );

    d2=distToCapsule2;


  float s2 = 1.1;
  for (int m2 = 0; m2 < 4; m2++) {
    vec3 a2 = mod(p * s2, 2.0) - 1.0;
    s2 *= 3.0;
    vec3 r2 = 1.0 - 3.1 * abs(a2*1.05);
    float c2 = sdCross(r2) / s2;
    d2 = max(d2, c2);
  }

  return smin(d,d2,.125);




}

vec3 normal(vec3 p) {
  float d = 0.01;
  return normalize(vec3(
    map(p + vec3(d, 0.0, 0.0)) - map(p - vec3(d, 0.0, 0.0)),
    map(p + vec3(0.0, d, 0.0)) - map(p - vec3(0.0, d, 0.0)),
    map(p + vec3(0.0, 0.0, d)) - map(p - vec3(0.0, 0.0, d))
  ));
}

vec3 raymarch(vec3 ro, vec3 rd) {
  vec3 p = ro;
  for (int i = 0; i < 64; i++) {
    float d = map(p);
    p += d * rd;
    if (d < 0.01) {
      vec3 n = normal(p);
      return n * 0.5 + 0.5;
      //return vec3(0.1) + vec3(0.95, 0.5, 0.5) * max(0.0, dot(n, normalize(vec3(1.0))));
    }
  }
  return vec3(1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

  vec2 st = (2.0 * fragCoord.xy - iResolution.xy) / min(iResolution.x, iResolution.y);

  vec3 ro = vec3(0.0, 0.0, 3.0);
  vec3 ta = vec3(0.0);
  vec3 z = normalize(ta - ro);
  vec3 x = normalize(cross(z, vec3(0.0, 1.0, 0.0)));
  vec3 y = normalize(cross(x, z));
  vec3 rd = normalize(st.x * x + st.y * y + 1.5 * z);

  vec3 c = raymarch(ro, rd);

  fragColor = vec4(c, 1.0);

}

float f(vec2 uv){return (abs(noise33(vec3(uv.xy*25.,uv.x+uv.y )).x-.5)*19.)+.4;}
vec2 grad( in vec2 x ){
    vec2 h = vec2( 0.01, 0.0 );
    return vec2( f(x+h.xy) - f(x-h.xy),
                 f(x+h.yx) - f(x-h.yx) )/(2.0*h.x);
}

float color( in vec2 x )
{
    float v = f( x );
    vec2  g = grad( x );
    float de = abs(v)/length(g);
    float eps = 1.;
    return smoothstep( 1.0*eps, 2.0*eps, de );
}


